import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

const DashboardInstructorScores = () => {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/evaluation/all');
        const rows = Array.isArray(res.data) ? res.data : [];
        setStudents(rows.filter(r => r && typeof r === 'object'));
      } catch (err) {
        console.error('❌ โหลดข้อมูลล้มเหลว:', err);
        setStudents([]);
      }
    };
    fetchData();
  }, []);

  const getResultColor = (result) => {
    if (!result) return 'text-gray-500';
    return result.toLowerCase() === 'ผ่าน' ? 'text-green-600 font-bold' : 'text-red-600 font-bold';
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] font-sans">
      <Header />

      <div className="px-[5%] py-[2%]">
        <h2 className="text-2xl font-extrabold text-[#130347] mb-6">
          📊 ผลการประเมินนิสิต
        </h2>

        {students.length === 0 ? (
          <p className="text-[#465d71] text-center">ไม่มีข้อมูลการประเมิน</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {students.map((stu, idx) => (
              <div
                key={stu?.evaluation_id ?? stu?.student_id ?? idx}
                className="bg-white rounded-2xl shadow-md p-6 flex flex-col gap-3"
              >
                {/* ชื่อและรหัสนิสิต */}
                <div className="flex justify-between items-start">
                  <div>
                    <p className="text-lg font-bold text-[#130347]">{stu.student_name}</p>
                    <p className="text-sm text-[#465d71]">รหัส: {stu.student_id}</p>
                  </div>
                  {/* ผลการประเมิน */}
                  <span className={`${getResultColor(stu.evaluation_result)}`}>
                    {stu.evaluation_result || 'ยังไม่ประเมิน'}
                  </span>
                </div>

                {/* รายละเอียดคะแนน */}
                <div className="grid grid-cols-2 gap-2 text-sm">
                  <p>คะแนนอาจารย์นิเทศ:</p>
                  <p className="font-semibold">{stu.supervisor_score ?? '-'}</p>
                  <p>คะแนนบริษัท:</p>
                  <p className="font-semibold">{stu.company_score ?? '-'}</p>
                  <p>รวม:</p>
                  <p className="font-semibold">{stu.total_score ?? '-'}</p>
                </div>

                {/* ปุ่มดูรายละเอียด */}
                <button
                  onClick={() => navigate(`/evaluation-detail/${stu.student_id}`)}
                  className="mt-auto bg-[#4691D3] hover:bg-[#225EC4] text-white px-4 py-2 rounded-lg text-sm transition"
                >
                  ดูรายละเอียด
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardInstructorScores;
