// src/pages/job/JobPostForm.jsx
import React, { useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

// แปลงลิงก์ Google Maps ธรรมดาให้เป็นแบบ embed (กันพิมพ์ผิดรูปแบบ)
const toEmbedMap = (raw) => {
  if (!raw) return '';
  const url = raw.trim();
  if (url.startsWith('https://www.google.com/maps/embed')) return url;

  // แปลงเคสยอดฮิต: place / q / goo.gl/maps -> ใช้ q + output=embed
  const encoded = encodeURIComponent(url);
  return `https://www.google.com/maps?q=${encoded}&output=embed`;
};

const JobPostForm = () => {
  const [submitting, setSubmitting] = useState(false);
  const [job, setJob] = useState({
    position: '',
    business_type: '',
    job_description: '',
    requirements: '',
    compensation: '',
    max_positions: '',
    address: '',
    google_maps_link: '',
    start_date: '',
    end_date: '',
    email: '',
    phone_number: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setJob((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitting) return;

    // ✅ Validate
    const phoneRegex = /^[0-9]{5,15}$/;
    if (!phoneRegex.test(job.phone_number)) {
      alert('❌ กรุณากรอกเบอร์โทรศัพท์ให้ถูกต้อง (เฉพาะตัวเลข 5–15 หลัก)');
      return;
    }
    if (Number(job.max_positions) < 0) {
      alert('❌ จำนวนที่รับสมัครต้องไม่ติดลบ');
      return;
    }
    if (Number(job.compensation) < 0) {
      alert('❌ ค่าตอบแทนต้องไม่ติดลบ');
      return;
    }
    if (job.start_date && job.end_date && new Date(job.start_date) > new Date(job.end_date)) {
      alert('❌ วันที่สิ้นสุดต้องไม่ก่อนวันที่เริ่มรับสมัคร');
      return;
    }

    try {
      setSubmitting(true);

      const company_id = localStorage.getItem('companyId');
      const formattedStartDate = job.start_date?.slice(0, 10) || null;
      const formattedEndDate = job.end_date?.slice(0, 10) || null;

      // แปลงลิงก์แผนที่เป็น embed เพื่อให้แสดงผลได้เหมือนหน้า Companies
      const google_maps_link = toEmbedMap(job.google_maps_link);

      const payload = {
        ...job,
        company_id,
        start_date: formattedStartDate,
        end_date: formattedEndDate,
        google_maps_link,
        compensation: job.compensation === '' ? null : Number(job.compensation),
        max_positions: job.max_positions === '' ? null : Number(job.max_positions),
      };

      await axios.post('http://localhost:5000/api/job_posting', payload);
      alert('✅ เพิ่มประกาศงานเรียบร้อยแล้ว');

      // เคลียร์ฟอร์ม
      setJob({
        position: '',
        business_type: '',
        job_description: '',
        requirements: '',
        compensation: '',
        max_positions: '',
        address: '',
        google_maps_link: '',
        start_date: '',
        end_date: '',
        email: '',
        phone_number: '',
      });
    } catch (err) {
      console.error('❌ ERROR:', err.response?.data || err.message);
      alert('❌ เกิดข้อผิดพลาดในการเพิ่มประกาศงาน');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="max-w-3xl mx-auto px-6 py-8">
        <form
          onSubmit={handleSubmit}
          className="bg-white border border-[#E6F0FF] rounded-2xl shadow-sm p-6"
        >
          <h2 className="text-2xl font-extrabold text-[#130347] mb-1">📋 เพิ่มประกาศงาน</h2>
          <p className="text-sm text-[#465d71] mb-5">
            กรอกข้อมูลตำแหน่งงาน รายละเอียด และช่องทางติดต่อบริษัทให้ครบถ้วน
          </p>

          {/* รายการฟิลด์ */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-[#130347]">ตำแหน่งที่เปิดรับ</label>
              <input
                name="position"
                value={job.position}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">ประเภทธุรกิจ</label>
              <input
                name="business_type"
                value={job.business_type}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">จำนวนที่รับสมัคร</label>
              <input
                name="max_positions"
                type="number"
                min="0"
                value={job.max_positions}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">ค่าตอบแทน (บาท/เดือน)</label>
              <input
                name="compensation"
                type="number"
                step="0.01"
                min="0"
                value={job.compensation}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">วันที่เริ่มรับสมัคร</label>
              <input
                name="start_date"
                type="date"
                value={job.start_date}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">วันสิ้นสุดรับสมัคร</label>
              <input
                name="end_date"
                type="date"
                value={job.end_date}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-[#130347]">ที่อยู่บริษัท</label>
              <input
                name="address"
                value={job.address}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-[#130347]">Google Maps Link</label>
              <input
                name="google_maps_link"
                value={job.google_maps_link}
                onChange={handleChange}
                placeholder="วางลิงก์จาก Google Maps (แนะนำแบบ Embed; วางลิงก์ปกติได้ ระบบจะพยายามแปลงให้)"
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
              />
              <p className="text-xs text-[#465d71] mt-1">
                วิธีได้ลิงก์ Embed: เปิดแผนที่ → แชร์ → ฝังแผนที่ → คัดลอกค่า <code>src</code>
              </p>
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-[#130347]">รายละเอียดงาน</label>
              <textarea
                name="job_description"
                value={job.job_description}
                onChange={handleChange}
                rows={4}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-[#130347]">คุณสมบัติที่ต้องการ</label>
              <textarea
                name="requirements"
                value={job.requirements}
                onChange={handleChange}
                rows={4}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">อีเมลสำหรับติดต่อ</label>
              <input
                name="email"
                type="email"
                value={job.email}
                onChange={handleChange}
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-[#130347]">เบอร์โทรศัพท์</label>
              <input
                name="phone_number"
                value={job.phone_number}
                onChange={handleChange}
                pattern="[0-9]{5,15}"
                title="กรุณากรอกเฉพาะตัวเลข 5–15 หลัก"
                className="w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2"
                required
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={submitting}
            className="mt-6 inline-flex items-center gap-2 rounded-full bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-3 font-semibold shadow disabled:opacity-60"
          >
            {submitting ? 'กำลังบันทึก…' : '➕ เพิ่ม'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default JobPostForm;
