import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Header from '../../components/Header';

const EvaluationSupervisorForm = () => {
  const { id } = useParams();
  const supervisorId = localStorage.getItem('supervisorId');
  const instructorId = localStorage.getItem('instructorId');
  const navigate = useNavigate();

  const [score, setScore] = useState({
    quality: '',
    behavior: '',
    skill: '',
    personality: '',
    content: '',
    qna: '',
    comment: ''
  });

  const maxScores = {
    quality: 20,
    behavior: 20,
    skill: 10,
    personality: 20,
    content: 20,
    qna: 10
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'comment') {
      setScore({ ...score, [name]: value });
      return;
    }
    if (value === '') {
      setScore({ ...score, [name]: '' });
      return;
    }
    const numericValue = parseInt(value);
    if (!isNaN(numericValue) && numericValue >= 0 && numericValue <= maxScores[name]) {
      setScore({ ...score, [name]: numericValue });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    for (const key in maxScores) {
      const val = parseInt(score[key]);
      if (isNaN(val) || val > maxScores[key]) {
        alert(`❌ ค่าคะแนน '${key}' ต้องไม่เกิน ${maxScores[key]} คะแนน`);
        return;
      }
    }

    try {
      await axios.post('http://localhost:5000/api/evaluation/submit', {
        student_id: id,
        role: 'supervisor',
        score_quality: score.quality,
        score_behavior: score.behavior,
        score_skill: score.skill,
        score_presentation: score.personality,
        score_content: score.content,
        score_answer: score.qna,
        supervisor_comment: score.comment,
        evaluation_date: new Date().toISOString().split('T')[0],
        supervisor_id: supervisorId,
        instructor_id: instructorId
      });

      alert('✅ ส่งผลการประเมินเรียบร้อยแล้ว');
      navigate('/supervisor/evaluation');
    } catch (err) {
      console.error('❌ เกิดข้อผิดพลาด:', err);
      alert('❌ ส่งผลล้มเหลว');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="bg-white shadow-lg rounded-xl max-w-3xl mx-auto p-6">
        <h2 className="text-2xl font-bold mb-6 text-center text-blue-700">📋 แบบประเมินนิสิตโดยอาจารย์นิเทศ</h2>
        <form onSubmit={handleSubmit} className="space-y-6">

          {/* ตารางที่ 1 */}
          <div>
            <h3 className="text-lg font-semibold text-gray-800 mb-2">📘 ตารางที่ 1: นิเทศงาน</h3>

            <FormGroup title="1. คุณภาพงาน (เต็ม 20)" name="quality" value={score.quality} onChange={handleChange}
              items={["1.1 งานที่ได้รับมอบหมายตรงกับสาขาของนิสิต", "1.2 คุณลักษณะงาน (Job description)"]} />

            <FormGroup title="2. พฤติกรรม (เต็ม 20)" name="behavior" value={score.behavior} onChange={handleChange}
              items={[
                "2.1 การแต่งกาย", "2.2 การแสดงความมีส่วนร่วมกับองค์กร", "2.3 สามารถทำงานร่วมกับผู้อื่นได้",
                "2.4 มีความคิดสร้างสรรค์ในการทำงาน", "2.5 กล้าแสดงความคิดเห็น", "2.6 มีความรับผิดชอบ"
              ]} />

            <FormGroup title="3. ทักษะ (เต็ม 10)" name="skill" value={score.skill} onChange={handleChange}
              items={["3.1 ความสามารถในการแก้ปัญหาเฉพาะหน้า", "3.2 วางแผนการปฏิบัติงาน"]} />
          </div>

          {/* ตารางที่ 2 */}
          <div>
            <h3 className="text-lg font-semibold text-gray-800 mb-2">📘 ตารางที่ 2: การนำเสนอผลงาน</h3>

            <FormGroup title="1. บุคลิกภาพ (เต็ม 20)" name="personality" value={score.personality} onChange={handleChange}
              items={["1.1 การแต่งกาย", "1.2 ความสามารถในการนำเสนอ", "1.3 การรักษาเวลา"]} />

            <FormGroup title="2. เนื้อหา (เต็ม 20)" name="content" value={score.content} onChange={handleChange}
              items={[
                "2.1 เนื้อหาครอบคลุม", "2.2 รูปภาพประกอบ", "2.3 เทคนิคการนำเสนอ",
                "2.4 ความคิดสร้างสรรค์", "2.5 ความสมบูรณ์ของรายงาน"
              ]} />

            <FormGroup title="3. การตอบคำถาม (เต็ม 10)" name="qna" value={score.qna} onChange={handleChange}
              items={["3.1 ตอบคำถามได้ตรงประเด็น"]} />
          </div>

          {/* ความคิดเห็น */}
          <div>
            <label className="font-semibold text-gray-700 block mb-2">💬 ความคิดเห็นเพิ่มเติม</label>
            <textarea name="comment" value={score.comment} onChange={handleChange}
              className="border border-gray-300 rounded w-full p-2" rows={3} />
          </div>

          <button type="submit" className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700">
            ✅ ส่งแบบประเมิน
          </button>
        </form>
      </div>
    </div>
  );
};

// 👇 component ย่อยเพื่อจัด layout แบบกล่องสวยงาม
const FormGroup = ({ title, items, name, value, onChange }) => (
  <div className="bg-gray-50 p-4 rounded-lg border mb-4">
    <label className="font-semibold text-gray-800">{title}</label>
    <ul className="text-sm text-gray-600 list-disc pl-6 mt-1 space-y-1">
      {items.map((item, idx) => <li key={idx}>{item}</li>)}
    </ul>
    <input
      type="number"
      name={name}
      value={value}
      onChange={onChange}
      className="mt-2 border p-1 w-24 rounded"
      required
    />
  </div>
);

export default EvaluationSupervisorForm;
