// src/pages/DashboardSupervisorProfile.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardSupervisorProfile = () => {
  const [form, setForm] = useState({
    supervisor_id: '',
    supervisor_name: '',
    email: '',
    phone_number: '',
    department: '',
    faculty: '',
    position: '',
    profile_image: ''
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const supervisorId = localStorage.getItem('supervisorId');

  useEffect(() => {
    if (supervisorId) {
      axios.get(`http://localhost:5000/api/supervisor/${supervisorId}`)
        .then((res) => {
          const data = res.data;
          setForm({
            supervisor_id: data.supervisor_id || '',
            supervisor_name: data.supervisor_name || '',
            email: data.email || '',
            phone_number: data.phone_number || '',
            department: data.department || '',
            faculty: data.faculty || '',
            position: data.position || '',
            profile_image: data.profile_image || ''
          });
        })
        .catch((err) => {
          console.error('❌ โหลดข้อมูลอาจารย์นิเทศล้มเหลว:', err);
        });
    }
  }, [supervisorId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let profileImageFilename = form.profile_image;

    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const res = await axios.post('http://localhost:5000/api/upload/profile-image', formData);
        profileImageFilename = res.data.filename;
      } catch (err) {
        console.error('❌ อัปโหลดรูปโปรไฟล์ล้มเหลว:', err);
        alert('เกิดข้อผิดพลาดในการอัปโหลดรูป');
        return;
      }
    }

    const updatedForm = { ...form, profile_image: profileImageFilename };

    axios.put(`http://localhost:5000/api/supervisor/${supervisorId}`, updatedForm)
      .then(() => {
        localStorage.setItem('profile_image', profileImageFilename);
        alert('✅ บันทึกข้อมูลเรียบร้อยแล้ว');
      })
      .catch((err) => {
        console.error('❌ บันทึกข้อมูลล้มเหลว:', err);
        alert('เกิดข้อผิดพลาดในการบันทึก');
      });
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] text-[#130347] font-sans">
      <Header />
      <div className="min-h-[calc(100vh-64px)] flex items-center justify-center p-6">
        <form
          onSubmit={handleSubmit}
          className="bg-white p-8 rounded-xl shadow-md max-w-4xl w-full"
        >
          <h2 className="text-3xl font-bold mb-6 text-center">โปรไฟล์ผู้ดูแล</h2>

          {/* รูปโปรไฟล์ */}
          <div className="flex flex-col items-center mb-6">
            {form.profile_image && (
              <img
                src={`http://localhost:5000/uploads/${form.profile_image}`}
                alt="รูปโปรไฟล์"
                className="w-28 h-28 rounded-full object-cover mb-3"
              />
            )}
            <label className="font-semibold mb-1">อัปโหลดรูปโปรไฟล์</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="file:mr-3 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-[#6EC7E2] file:text-white hover:file:bg-[#4691D3]"
            />
          </div>

          {/* ฟอร์มข้อมูล */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block mb-1 font-semibold">รหัสผู้ดูแล</label>
              <input
                name="supervisor_id"
                value={form.supervisor_id}
                disabled
                className="w-full border rounded px-3 py-2 bg-gray-100"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold">ชื่อ</label>
              <input
                name="supervisor_name"
                value={form.supervisor_name}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
                required
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold">อีเมล</label>
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
                required
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold">เบอร์โทร</label>
              <input
                name="phone_number"
                value={form.phone_number}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold">ภาควิชา</label>
              <input
                name="department"
                value={form.department}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
              />
            </div>
            <div>
              <label className="block mb-1 font-semibold">คณะ</label>
              <input
                name="faculty"
                value={form.faculty}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
              />
            </div>
            <div className="md:col-span-2">
              <label className="block mb-1 font-semibold">ตำแหน่ง</label>
              <input
                name="position"
                value={form.position}
                onChange={handleChange}
                className="w-full border rounded px-3 py-2 bg-gray-50"
              />
            </div>
          </div>

          {/* ปุ่มบันทึก */}
          <div className="mt-6">
            <button
              type="submit"
              className="w-full bg-[#225EC4] hover:bg-[#063D8C] text-white font-semibold py-2 px-4 rounded-full"
            >
              💾 บันทึก
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default DashboardSupervisorProfile;
