import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EvaluationSupervisorForm = () => {
  const { id } = useParams(); // student_id
  const supervisorId = localStorage.getItem('supervisorId');
  const instructorId = localStorage.getItem('instructorId');
  const navigate = useNavigate();

  const [score, setScore] = useState({
    quality: '',
    behavior: '',
    skill: '',
    personality: '',
    content: '',
    qna: '',
    comment: ''
  });

  const maxScores = {
    quality: 20,
    behavior: 20,
    skill: 10,
    personality: 20,
    content: 20,
    qna: 10
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'comment') {
      setScore({ ...score, [name]: value });
      return;
    }

    if (value === '') {
      setScore({ ...score, [name]: '' });
      return;
    }

    const numericValue = parseInt(value);
    if (!isNaN(numericValue) && numericValue >= 0 && numericValue <= maxScores[name]) {
      setScore({ ...score, [name]: numericValue });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    for (const key in maxScores) {
      const val = parseInt(score[key]);
      if (isNaN(val) || val > maxScores[key]) {
        alert(`❌ ค่าคะแนน '${key}' ต้องไม่เกิน ${maxScores[key]} คะแนน`);
        return;
      }
    }

    try {
      await axios.post('http://localhost:5000/api/evaluation/submit', {
        student_id: id,
        role: 'supervisor',
        score_quality: score.quality,
        score_behavior: score.behavior,
        score_skill: score.skill,
        score_presentation: score.personality,
        score_content: score.content,
        score_answer: score.qna,
        supervisor_comment: score.comment,
        evaluation_date: new Date().toISOString().split('T')[0],
        supervisor_id: supervisorId,
        instructor_id: instructorId
      });

      alert('✅ ส่งผลการประเมินเรียบร้อยแล้ว');
      navigate('/supervisor/evaluation');
    } catch (err) {
      console.error('❌ เกิดข้อผิดพลาด:', err);
      alert('❌ ส่งผลล้มเหลว');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">📋 แบบประเมินนิสิตโดยอาจารย์นิเทศ</h2>
      <form onSubmit={handleSubmit}>
        <h3 className="font-semibold mt-4 mb-2">ตารางที่ 1 ตารางการนิเทศงานสำหรับประเมินนิสิต</h3>

        <div className="mb-4">
          <label className="font-semibold">1. คุณภาพงาน (เต็ม 20)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>1.1 งานที่ได้รับมอบหมายตรงกับสาขาของนิสิต</div>
            <div>1.2 คุณลักษณะงาน (Job description)</div>
          </div>
          <input type="number" name="quality" value={score.quality} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <div className="mb-4">
          <label className="font-semibold">2. พฤติกรรม (เต็ม 20)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>2.1 การแต่งกาย</div>
            <div>2.2 การแสดงความมีส่วนร่วมกับองค์กร</div>
            <div>2.3 สามารถทำงานร่วมกับผู้อื่นได้</div>
            <div>2.4 มีความคิดสร้างสรรค์ในการทำงาน</div>
            <div>2.5 กล้าแสดงความคิดเห็น</div>
            <div>2.6 มีความรับผิดชอบปฏิบัติงานได้เสร็จตามระยะเวลาที่กำหนด</div>
          </div>
          <input type="number" name="behavior" value={score.behavior} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <div className="mb-4">
          <label className="font-semibold">3. ทักษะ (เต็ม 10)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>3.1 ความสามารถในการแก้ปัญหาเฉพาะหน้า</div>
            <div>3.2 วางแผนการปฏิบัติงานที่ได้รับมอบหมาย</div>
          </div>
          <input type="number" name="skill" value={score.skill} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <h3 className="font-semibold mt-6 mb-2">ตารางที่ 2 การนำเสนอผลงาน</h3>

        <div className="mb-4">
          <label className="font-semibold">1. บุคลิกภาพ (เต็ม 20)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>1.1 การแต่งกาย</div>
            <div>1.2 ความสามารถในการนำเสนอ</div>
            <div>1.3 การรักษาเวลา</div>
          </div>
          <input type="number" name="personality" value={score.personality} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <div className="mb-4">
          <label className="font-semibold">2. เนื้อหา (เต็ม 20)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>2.1 เนื้อหาครอบคลุม</div>
            <div>2.2 รูปภาพประกอบการนำเสนอ</div>
            <div>2.3 เทคนิคการนำเสนอผลงาน</div>
            <div>2.4 ความคิดสร้างสรรค์ในการนำเสนอผลงาน</div>
            <div>2.5 ความสมบูรณ์ของรายงาน</div>
          </div>
          <input type="number" name="content" value={score.content} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <div className="mb-4">
          <label className="font-semibold">3. การตอบคำถาม (เต็ม 10)</label>
          <div className="pl-12 text-sm text-gray-600">
            <div>3.1 ตอบคำถามได้ตรงประเด็น</div>
          </div>
          <input type="number" name="qna" value={score.qna} onChange={handleChange} className="border p-1 w-20 mt-2" required />
        </div>

        <div className="mb-4 mt-4">
          <label className="font-semibold">ความคิดเห็นเพิ่มเติม</label>
          <textarea name="comment" value={score.comment} onChange={handleChange} className="border p-2 w-full" rows="3" />
        </div>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">✅ ส่งแบบประเมิน</button>
      </form>
    </div>
  );
};

export default EvaluationSupervisorForm;
