import React, { useEffect, useState } from 'react';
import Header from '../../components/Header';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DashboardStudentStatus = () => {
  const studentId = localStorage.getItem('studentId');
  const [applications, setApplications] = useState([]);
  const [confirmedStatus, setConfirmedStatus] = useState({});
  const navigate = useNavigate();

  const formatDate = (dateStr) => dateStr?.split('T')[0];

  useEffect(() => {
    if (!studentId) return;

    axios.get(`http://localhost:5000/api/student/status/history/${studentId}`)
      .then(res => {
        setApplications(res.data);
        // กำหนดสถานะที่ยืนยันแล้ว
        const confirmedMap = {};
        res.data.forEach(app => {
          if (app.status === 'รับ' && app.confirmed === 1) {
            confirmedMap[app.job_posting_id] = true;
          }
        });
        setConfirmedStatus(confirmedMap);
      })
      .catch(err => {
        console.error("❌ โหลดข้อมูลล้มเหลว:", err);
        setApplications([]);
      });
  }, [studentId]);

  const getStatusColor = (status) => {
    switch (status) {
      case 'รอพิจารณา': return 'text-yellow-600';
      case 'รับ': return 'text-green-600';
      case 'ไม่รับ': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const handleViewDetails = (jobId) => {
    if (!jobId) {
      alert('❌ ไม่พบ job_posting_id');
      return;
    }
    navigate(`/job-detail/${jobId}`, {
      state: { fromStatusPage: true }
    });
  };

  const handleConfirm = async (jobId) => {
  try {
    const res = await axios.post('http://localhost:5000/api/internship/confirm', {
      student_id: studentId,
      job_posting_id: jobId
    });
    alert('✅ ยืนยันการฝึกงานสำเร็จ');

    // ✅ อัปเดต state ยืนยันใน confirmedStatus
    setConfirmedStatus(prev => ({ ...prev, [jobId]: true }));

    // ✅ อัปเดตข้อมูลใน applications ด้วย
    setApplications(prevApps =>
      prevApps.map(app =>
        app.job_posting_id === jobId ? { ...app, confirmed: 1 } : app
      )
    );
  } catch (err) {
    console.error("❌ ยืนยันล้มเหลว:", err);
    const message = err.response?.data?.message || 'เกิดข้อผิดพลาดในการยืนยันการฝึกงาน';
    alert("❌ " + message);
  }
};


  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6 max-w-5xl mx-auto">
      

        <div className="bg-white text-black p-6 rounded-lg shadow-md overflow-x-auto">
          {applications.length === 0 ? (
            <p>ไม่มีข้อมูลการสมัครฝึกงาน</p>
          ) : (
            <table className="w-full table-auto border border-gray-300">
              <thead>  <h1 className="text-3xl font-bold mb-4">📄 สถานะการฝึกงาน</h1>
                <tr className="bg-gray-100">
                  
                  <th className="border p-2">บริษัท</th>
                  <th className="border p-2">ตำแหน่ง</th>
                  <th className="border p-2">วันที่สมัคร</th>
                  <th className="border p-2">สถานะ</th>
                  <th className="border p-2">เพิ่มเติม</th>
                  <th className="border p-2">ยืนยัน</th>
                </tr>
              </thead>
              <tbody>
                {applications.map((app, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="border p-2">{app.company_name}</td>
                    <td className="border p-2">{app.position}</td>
                    <td className="border p-2">{formatDate(app.apply_date)}</td>
                    <td className={`border p-2 font-semibold ${getStatusColor(app.status)}`}>
                      {app.status}
                    </td>
                    <td className="border p-2 text-center">
                      <button
                        onClick={() => handleViewDetails(app.job_posting_id)}
                        className="text-blue-600 hover:underline"
                      >
                        ดูเพิ่มเติม
                      </button>
                    </td>
                   <td className="border p-2 text-center">
                    {app.status === 'รับ' && confirmedStatus[app.job_posting_id] ? (
                      <span className="text-green-700 font-bold flex items-center justify-center gap-1">
                        <input type="checkbox" checked readOnly className="accent-green-600" />
                        ยืนยันแล้ว
                      </span>
                    ) : app.status === 'รับ' ? (
                      <button
                        onClick={() => handleConfirm(app.job_posting_id)}
                        className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                      >
                        ยืนยันฝึกงาน
                      </button>
                    ) : (
                      "-"
                    )}
                  </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardStudentStatus;
