          ].map((item, i) => (
            <div key={i} className="mb-4 border-b pb-2">
              <label className="block mb-1">{i + 1}. {item}</label>
              <div className="flex space-x-6">
                {[5, 4, 3, 2, 1].map((val) => (
                  <label key={val} className="flex items-center space-x-1">
                    <input
