import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Header from '../components/Header';

const EvaluationCompanyForm = () => {
  const { id } = useParams(); // student_id
  const navigate = useNavigate();
  const companyId = localStorage.getItem('companyId');

  const [student, setStudent] = useState(null);
  const [scores, setScores] = useState({});
  const [companyComment, setCompanyComment] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/student/${id}`)
      .then(res => setStudent(res.data))
      .catch(err => console.error('❌ โหลดนิสิตล้มเหลว:', err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('absent_days')) {
      const days = parseInt(value);
      setScores(prev => ({ ...prev, [name]: isNaN(days) ? '' : days }));
    }
  };

  const handleRadio = (name, value) => {
    setScores(prev => ({ ...prev, [name]: value }));
  };

  const calcTotalScore = () => {
    let total = 0;
    for (let i = 1; i <= 10; i++) total += parseInt(scores[`p${i}`]) || 0;
    for (let i = 1; i <= 10; i++) total += parseInt(scores[`w${i}`]) || 0;
    const weights = [2, 2, 4, 1];
    let penalty = 0;
    for (let i = 0; i < 4; i++) {
      penalty += (parseInt(scores[`absent_days${i}`]) || 0) * weights[i];
    }
    total += Math.max(0, 20 - penalty);
    return total;
  };

  const handleSubmit = async () => {
    const totalScore = calcTotalScore();

    try {
      await axios.post('http://localhost:5000/api/evaluation/submit', {
        student_id: id,
        company_score: totalScore,
        company_comment: companyComment,
        company_id: companyId,
        evaluation_date: new Date().toISOString().split('T')[0],
        role: 'company'
      });
      alert("✅ ส่งแบบประเมินสำเร็จ");
      navigate('/company/evaluation');
    } catch (err) {
      console.error("❌ บันทึกล้มเหลว:", err);
      alert("เกิดข้อผิดพลาด");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="min-h-[calc(100vh-64px)] flex items-center justify-center p-6">
        <div className="bg-white text-black p-6 rounded-lg shadow-md max-w-4xl w-full">
          <h2 className="text-2xl font-bold text-orange-600 mb-4">📋 แบบประเมินนิสิตโดยสถานประกอบการ</h2>

          {/* Section 1: Personality */}
          <h3 className="mt-6 mb-2 font-semibold">1. บุคลิกภาพ (รวม 50 คะแนน)</h3>
          {[
            "ตรงต่อเวลา", "ความสะอาดเรียบร้อย", "ความสุภาพ", "ความขยัน", "กระตือรือร้น",
            "ความรับผิดชอบ", "การปรับตัว", "ความมั่นใจในตนเอง", "มนุษย์สัมพันธ์", "ความมีระเบียบวินัย"
          ].map((item, i) => (
            <div key={i} className="mb-4 border-b pb-2">
              <label className="block mb-1">{i + 1}. {item}</label>
              <div className="flex space-x-6">
                {[5, 4, 3, 2, 1].map((val) => (
                  <label key={val} className="flex items-center space-x-1">
                    <input
                      type="radio"
                      name={`p${i + 1}`}
                      value={val}
                      checked={scores[`p${i + 1}`] === val}
                      onChange={() => handleRadio(`p${i + 1}`, val)}
                    />
                    <span className="text-sm text-gray-600">{val}</span>
                  </label>
                ))}
              </div>
            </div>
          ))}

          {/* Section 2: Work performance */}
          <h3 className="mt-6 mb-2 font-semibold">2. การปฏิบัติงาน (รวม 50 คะแนน)</h3>
          {[
            "ความรู้ในงาน", "ความสามารถในการทำงาน", "การวางแผน/บริหารเวลา", "การเรียนรู้เร็ว", "ความละเอียดรอบคอบ",
            "การปฏิบัติงานอย่างต่อเนื่อง", "การปฏิบัติตามคำแนะนำ", "การทำงานร่วมกับผู้อื่น", "ความคิดริเริ่มสร้างสรรค์", "คุณภาพผลงาน"
          ].map((item, i) => (
            <div key={i} className="mb-4 border-b pb-2">
              <label className="block mb-1">{i + 1}. {item}</label>
              <div className="flex space-x-6">
                {[5, 4, 3, 2, 1].map((val) => (
                  <label key={val} className="flex items-center space-x-1">
                    <input
                      type="radio"
                      name={`w${i + 1}`}
                      value={val}
                      checked={scores[`w${i + 1}`] === val}
                      onChange={() => handleRadio(`w${i + 1}`, val)}
                    />
                    <span className="text-sm text-gray-600">{val}</span>
                  </label>
                ))}
              </div>
            </div>
          ))}

          {/* Section 3: Attendance deductions */}
          <h3 className="mt-6 font-semibold">3. การไม่มาฝึกงาน (หักคะแนนจาก 20)</h3>
          {["ลาป่วย", "ลากิจ", "ขาดงาน", "มาสาย"].map((label, i) => (
            <div key={i} className="mb-3">
              <label>{label}:</label>
              <input
                type="number"
                name={`absent_days${i}`}
                min={0}
                step={1}
                placeholder="วัน"
                className="border p-1 w-24 ml-2 rounded"
                onChange={handleChange}
              />
              <span className="ml-2 text-sm text-gray-500">หักวันละ {[2, 2, 4, 1][i]} คะแนน</span>
            </div>
          ))}

          {/* Section 4: Comment */}
          <h3 className="mt-6 font-semibold">ความคิดเห็นเพิ่มเติม</h3>
          <textarea
            className="border p-2 w-full rounded mt-1"
            rows={4}
            value={companyComment}
            onChange={(e) => setCompanyComment(e.target.value)}
          />

          <div className="mt-6 text-center">
            <button
              onClick={handleSubmit}
              className="bg-indigo-600 text-white px-6 py-2 rounded hover:bg-indigo-700"
            >
              ✅ ส่งแบบประเมิน
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EvaluationCompanyForm;
