import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardAdminCompanies = () => {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/admin/companies/approved')
      .then(res => {
        setCompanies(res.data);
      })
      .catch(err => {
        console.error('❌ ไม่สามารถดึงข้อมูลบริษัทได้:', err);
      });
  }, []);

  const handleDelete = (companyId) => {
    if (window.confirm('คุณต้องการลบบริษัทนี้หรือไม่?')) {
      axios.delete(`http://localhost:5000/api/admin/delete-company/${companyId}`)
        .then(res => {
          alert(res.data.message);
          setCompanies(companies.filter(company => company.company_id !== companyId));
        })
        .catch(err => {
          console.error('❌ เกิดข้อผิดพลาดในการลบบริษัท:', err);
          alert('ไม่สามารถลบได้');
        });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6 mt-10 max-w-4xl mx-auto bg-white text-black rounded-lg shadow">
        <h1 className="text-2xl font-bold mb-6 text-indigo-700">🏢 บริษัทที่ได้รับการอนุมัติ</h1>

        {companies.length === 0 ? (
          <p>ไม่มีบริษัทที่ได้รับการอนุมัติ</p>
        ) : (
          <div className="grid gap-6">
            {companies.map(company => (
              <div key={company.company_id} className="bg-gray-50 p-4 rounded shadow flex flex-col md:flex-row gap-4 items-start md:items-center justify-between">
                <div className="flex gap-4 items-start">
                  {company.company_logo && (
                    <img
                      src={`http://localhost:5000/uploads/${company.company_logo}`}
                      alt="Logo"
                      className="w-24 h-24 object-cover rounded border"
                    />
                  )}
                  <div className="text-sm space-y-1">
                    <p><strong>รหัสบริษัท:</strong> {company.company_id}</p>
                    <p><strong>ชื่อ:</strong> {company.company_name}</p>
                    <p><strong>ประเภทธุรกิจ:</strong> {company.business_type}</p>
                    <p><strong>อีเมล:</strong> {company.contact_email}</p>
                    <p><strong>ผู้ติดต่อ:</strong> {company.contact_name}  {company.phone_number}</p>
                    <p><strong>เว็บไซต์:</strong> {company.website }</p>
                    <p><strong>ที่อยู่:</strong> {company.address}</p>
                    <p><strong>Maps:</strong> {company.google_maps_link }</p>
                    <p><strong>สร้างเมื่อ:</strong> {new Date(company.created_date).toLocaleString('th-TH', { timeZone: 'Asia/Bangkok' })}</p>
<p><strong>อัปเดตล่าสุด:</strong> {new Date(company.last_updated).toLocaleString('th-TH', { timeZone: 'Asia/Bangkok' })}</p>

                  </div>
                </div>
                <div className="mt-4 md:mt-0 self-end md:self-center">
                  <button
                    onClick={() => handleDelete(company.company_id)}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                  >
                    ลบ
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardAdminCompanies;
