// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      switch (role) {
        case 'student': localStorage.setItem('studentId', id); navigate('/dashboard/student'); break;
        case 'company': localStorage.setItem('companyId', id); navigate('/dashboard/company'); break;
        case 'instructor': localStorage.setItem('instructorId', id); navigate('/dashboard/instructor'); break;
        case 'supervisor': localStorage.setItem('supervisorId', id); navigate('/dashboard/supervisor'); break;
        case 'admin': localStorage.setItem('adminId', id); navigate('/admin/approvals'); break;
        default: setMessage('บทบาทผู้ใช้ไม่ถูกต้อง'); return;
      }

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-200 via-blue-300 to-blue-400">
      <div className="w-full max-w-5xl bg-white rounded-3xl shadow-2xl overflow-hidden grid grid-cols-1 md:grid-cols-2">
        
        {/* ฝั่งซ้าย โลโก้ + พื้นหลังไล่สีฟ้า */}
        <div className="relative flex items-center justify-center bg-gradient-to-br from-blue-500 to-sky-400 p-10">
          <div className="text-center text-white">
            <img
              src="/uploads/InternConnectLogo.png" 

              alt="InternConnect"
              className="mx-auto mb-6 w-28 h-28 object-contain"
            />
            <h1 className="text-3xl font-bold tracking-wide">InternConnect</h1>
            <p className="mt-3 text-white/80 text-sm">
              ระบบจัดการฝึกงานครบวงจร
            </p>
          </div>
        </div>

        {/* ฝั่งขวา ฟอร์ม Login */}
        <div className="p-10 flex flex-col justify-center bg-white">
          <h2 className="text-2xl font-bold text-blue-600 mb-6">Login</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <div className="flex items-center justify-between text-sm">
              <span />
              <Link to="/forgot-password" className="text-blue-500 hover:underline">
                ลืมรหัสผ่าน?
              </Link>
            </div>
            <button
              type="submit"
              className="w-full py-3 rounded-xl font-semibold bg-gradient-to-r from-blue-500 to-sky-400 text-white hover:opacity-90 transition"
            >
              LOGIN
            </button>
            <p className="text-sm text-center">
              ยังไม่มีบัญชี?{' '}
              <Link to="/register" className="text-blue-500 hover:underline">
                สมัครสมาชิก
              </Link>
            </p>
            {message && (
              <p className="text-xs text-red-600 text-center">{message}</p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
