    setUserRole(role);

    axios
      .get(`http://localhost:5000/api/job_posting/${id}`)
      .then(res => setJob(res.data))
      .catch(err => {
        console.error('‚ùå ‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•:', err);
        alert('‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô');
        navigate('/dashboard/company');
      });

    if (role === 'student' && student_id) {
      axios
        .get(`http://localhost:5000/api/job_posting/check-application`, {
          params: { student_id, job_posting_id: id },
        })
        .then(res => setHasApplied(res.data.applied))
        .catch(err => console.error('‚ùå ‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö‡∏Å‡∏≤‡∏£‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err));
    }
  }, [id, navigate]);

  const handleApply = async () => {
    const student_id = localStorage.getItem('studentId');
    if (!student_id || !resumeFile) return alert('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÅ‡∏ô‡∏ö‡πÑ‡∏ü‡∏•‡πå Resume');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('student_id', student_id);
    formData.append('job_posting_id', id);

    try {
      await axios.post('http://localhost:5000/api/job_posting/apply', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('‚úÖ ‡∏™‡πà‡∏á‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß');
      setShowModal(false);
      setHasApplied(true);
    } catch (err) {
      console.error('‚ùå ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:', err);
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏™‡∏°‡∏±‡∏Ñ‡∏£');
    }
  };

  if (!job) {
    return (
      <div className="min-h-screen bg-[#9AE5F2]">
        <Header />
        <div className="text-center p-10 text-[#130347]">‚è≥ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î...</div>
      </div>
    );
  }

  const isEmbedMap =
    typeof job.google_maps_link === 'string' &&
    job.google_maps_link.startsWith('https://www.google.com/maps/embed');

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="w-full max-w-screen-lg mx-auto px-4 lg:px-8 py-8">
        {/* ‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á + ‡∏õ‡∏∏‡πà‡∏°‡∏¢‡πâ‡∏≠‡∏ô‡∏Å‡∏•‡∏±‡∏ö */}
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-2xl font-extrabold text-[#130347]">üìã ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô</h2>
            <p className="text-sm text-[#465d71]">‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏á‡∏≤‡∏ô‡πÅ‡∏•‡∏∞‡∏Å‡∏≤‡∏£‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó</p>
          </div>
          <button
            onClick={() => navigate(-1)}
            className="rounded-full bg-white/80 hover:bg-white text-[#130347] px-4 py-2 text-sm font-semibold border border-[#E6F0FF] shadow-sm"
          >
            ‚Üê ‡∏Å‡∏•‡∏±‡∏ö
          </button>
        </div>

        {/* ‡∏Å‡∏≤‡∏£‡πå‡∏î‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á */}
        <div className="bg-white rounded-2xl border border-[#E6F0FF] shadow-sm p-5 mb-6">
          <div className="flex items-start gap-4">
            {job.company_logo ? (
              <img
                src={`http://localhost:5000/uploads/${job.company_logo}`}
