// src/pages/dashboard/DashboardInstructorStudents.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

const DashboardInstructorStudents = () => {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/instructor/students')
      .then(res => {
        console.log("📦 ได้ข้อมูลนิสิต:", res.data);
        setStudents(res.data);
      })
      .catch(err => {
        console.error("❌ ไม่สามารถโหลดข้อมูลนิสิต:", err);
      });
  }, []);

  const handleViewDetails = (studentId) => {
    navigate(`/student-detail/${studentId}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="p-6 mt-10 max-w-6xl mx-auto bg-white text-black rounded-lg shadow">
        <h2 className="text-3xl font-bold mb-6 text-purple-700">🎓 รายชื่อนิสิต</h2>

        {students.length === 0 ? (
          <p className="text-gray-600">ไม่มีข้อมูลนิสิต</p>
        ) : (
          <table className="w-full table-auto border border-gray-300 text-sm">
            <thead className="bg-indigo-100 text-indigo-800">
              <tr>
                <th className="px-4 py-2 border">รหัสนิสิต</th>
                <th className="px-4 py-2 border">ชื่อนิสิต</th>
                <th className="px-4 py-2 border">อีเมล</th>
                <th className="px-4 py-2 border">เพิ่มเติม</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.student_id} className="hover:bg-gray-100">
                  <td className="px-4 py-2 border">{student.student_id}</td>
                  <td className="px-4 py-2 border">{student.student_name}</td>
                  <td className="px-4 py-2 border">{student.email}</td>
                  <td className="px-4 py-2 border text-center">
                    <button
                      onClick={() => handleViewDetails(student.student_id)}
                      className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                    >
                      ดูเพิ่มเติม
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default DashboardInstructorStudents;
