import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const JobDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const fromStatusPage = location.state?.fromStatusPage || false;

  const [job, setJob] = useState(null);
  const [userRole, setUserRole] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [resumeFile, setResumeFile] = useState(null);

  useEffect(() => {
    const role = localStorage.getItem('role');
    setUserRole(role);

    axios.get(`http://localhost:5000/api/job_posting/${id}`)
      .then(res => setJob(res.data))
      .catch(err => {
        console.error('❌ ไม่พบข้อมูล:', err);
        alert('ไม่พบข้อมูลประกาศงาน');
        navigate('/dashboard/company');
      });
  }, [id, navigate]);

  const handleApply = async () => {
    const student_id = localStorage.getItem('studentId');
    if (!student_id || !resumeFile) return alert('กรุณาแนบไฟล์ Resume');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('student_id', student_id);
    formData.append('job_posting_id', id);

    try {
      await axios.post('http://localhost:5000/api/job_posting/apply', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('✅ ส่งใบสมัครสำเร็จแล้ว');
      setShowModal(false);
    } catch (err) {
      console.error('❌ สมัครไม่สำเร็จ:', err);
      alert('เกิดข้อผิดพลาดในการสมัคร');
    }
  };

  if (!job) return <div className="text-center p-6 text-white">⏳ กำลังโหลด...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-400 to-indigo-600 text-white font-sans">
      <Header />
      <div className="p-6 max-w-4xl mx-auto bg-white text-black rounded shadow">
        <h2 className="text-2xl font-bold mb-4 text-indigo-700">📋 รายละเอียดประกาศงาน</h2>
        <p><strong>ตำแหน่งที่เปิดรับ:</strong> {job.position}</p>
        <p><strong>ประเภทธุรกิจ:</strong> {job.business_type}</p>
        <p><strong>รายละเอียดงาน:</strong> {job.job_description}</p>
        <p><strong>คุณสมบัติที่ต้องการ:</strong> {job.requirements}</p>
        <p><strong>ค่าตอบแทน:</strong> {job.compensation} บาท/เดือน</p>
        <p><strong>จำนวนที่รับสมัคร:</strong> {job.max_positions}</p>
        <p><strong>ที่อยู่บริษัท:</strong> {job.address}</p>
        <p><strong>Google Maps:</strong> <a href={job.google_maps_link} className="text-blue-600 underline" target="_blank" rel="noopener noreferrer">เปิดแผนที่</a></p>
        <p><strong>วันที่เริ่มรับสมัคร:</strong> {new Date(job.start_date).toLocaleDateString('th-TH')}</p>
        <p><strong>วันสิ้นสุดรับสมัคร:</strong> {new Date(job.end_date).toLocaleDateString('th-TH')}</p>
        <p><strong>อีเมลสำหรับติดต่อ:</strong> {job.email}</p>
        <p><strong>เบอร์โทรศัพท์:</strong> {job.phone_number}</p>

        {/* 👨‍💼 Company Role: ปุ่มแก้ไข/ลบ */}
        {userRole === 'company' && (
          <div className="mt-6 flex gap-2">
            <button
              onClick={() => navigate(`/job-edit/${id}`)}
              className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
            >
              ✏️ แก้ไข
            </button>
            <button
              onClick={async () => {
                if (window.confirm('คุณแน่ใจหรือไม่ว่าต้องการลบประกาศนี้?')) {
                  try {
                    await axios.delete(`http://localhost:5000/api/job_posting/${id}`);
                    alert('✅ ลบประกาศเรียบร้อยแล้ว');
                    navigate('/dashboard/company');
                  } catch (err) {
                    console.error('❌ ลบไม่สำเร็จ:', err);
                    alert('เกิดข้อผิดพลาดในการลบ');
                  }
                }
              }}
              className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
            >
              🗑️ ลบ
            </button>
          </div>
        )}

        {/* 👩‍🎓 Student Role: ปุ่มสมัคร (แสดงเฉพาะถ้าไม่มาจาก status page) */}
        {userRole === 'student' && !fromStatusPage && (
          <div className="mt-6">
            <button
              onClick={() => setShowModal(true)}
              className="bg-green-500 text-white px-6 py-2 rounded hover:bg-green-600"
            >
              ✅ สมัคร
            </button>
          </div>
        )}
      </div>

      {/* ✅ Modal สำหรับแนบ Resume */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center z-50">
          <div className="bg-gray-700 rounded-xl p-8 w-96 text-white text-center">
            <h2 className="text-2xl font-bold mb-4">ส่ง Resume</h2>
            <input
              type="file"
              accept=".pdf"
              onChange={e => setResumeFile(e.target.files[0])}
              className="mb-4 bg-white text-black rounded p-2 w-full"
            />
            <div className="flex justify-around mt-4">
              <button onClick={() => setShowModal(false)} className="bg-red-500 px-6 py-2 rounded text-white hover:bg-red-600">ยกเลิก</button>
              <button onClick={handleApply} className="bg-green-500 px-6 py-2 rounded text-white hover:bg-green-600">ยืนยัน</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default JobDetailPage;
