// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      const go = (path, key) => { localStorage.setItem(key, id); navigate(path); };
      if (role === 'student') go('/dashboard/student', 'studentId');
      else if (role === 'company') go('/dashboard/company', 'companyId');
      else if (role === 'instructor') go('/dashboard/instructor', 'instructorId');
      else if (role === 'supervisor') go('/dashboard/supervisor', 'supervisorId');
      else if (role === 'admin') go('/admin/approvals', 'adminId');
      else return setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2 bg-white">
      {/* Left brand panel (gradient + pattern) */}
      <div className="relative hidden md:flex items-center justify-center overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-blue-700 via-sky-600 to-blue-500"></div>
        {/* soft shapes */}
        <div className="absolute -top-16 -left-16 w-72 h-72 bg-white/10 rounded-full blur-3xl" />
        <div className="absolute -bottom-24 -right-20 w-80 h-80 bg-white/10 rounded-full blur-3xl" />
        {/* subtle grid pattern */}
        <div className="absolute inset-0 opacity-10"
             style={{backgroundImage:'linear-gradient(transparent 23px, rgba(255,255,255,.12) 24px),linear-gradient(90deg, transparent 23px, rgba(255,255,255,.12) 24px)', backgroundSize:'24px 24px'}} />
        <div className="relative z-10 text-center text-white px-8">
          <img src="/uploads/InternConnectLogo.png" alt="InternConnect"
               className="mx-auto mb-6 w-24 h-24 object-contain drop-shadow-md" />
          <h1 className="text-3xl font-semibold tracking-wide">InternConnect</h1>
          <p className="mt-2 text-white/80">เชื่อมต่อ นิสิต • บริษัท • อาจารย์ • ผู้ควบคุมงาน</p>
        </div>
      </div>

      {/* Right form panel */}
      <div className="flex items-center justify-center p-6 md:p-10">
        <div className="w-full max-w-md bg-white rounded-2xl shadow-xl border border-slate-100 p-8">
          <div className="md:hidden text-center mb-4">
            <img src="/uploads/InternConnectLogo.png" alt="InternConnect" className="mx-auto w-14 h-14" />
          </div>
          <h2 className="text-2xl font-bold text-slate-800 mb-1">เข้าสู่ระบบ</h2>
          <p className="text-sm text-slate-500 mb-6">กรุณากรอกอีเมลและรหัสผ่าน</p>

          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email" name="email" placeholder="อีเมล" value={form.email} onChange={handleChange} required
              className="w-full px-4 py-3 rounded-xl border border-slate-200 bg-slate-50 focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400"
            />
            <input
              type="password" name="password" placeholder="รหัสผ่าน" value={form.password} onChange={handleChange} required
              className="w-full px-4 py-3 rounded-xl border border-slate-200 bg-slate-50 focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400"
            />

            <div className="flex items-center justify-between text-sm">
              <span />
              <Link to="/forgot-password" className="text-sky-700 hover:underline">ลืมรหัสผ่าน?</Link>
            </div>

            <button type="submit"
              className="w-full py-3 rounded-xl font-semibold bg-gradient-to-r from-sky-600 to-blue-700 text-white hover:opacity-95 active:opacity-90 transition">
              LOGIN
            </button>

            <p className="text-sm text-center text-slate-600">
              ยังไม่มีบัญชี? <Link to="/register" className="text-sky-700 hover:underline">สมัครสมาชิก</Link>
            </p>

            {message && <p className="text-xs text-red-600 text-center">{message}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
