import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardAdminCompanies = () => {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/admin/companies/approved')
      .then(res => {
        console.log("✅ ได้ข้อมูลบริษัท:", res.data); // 👉 log ข้อมูลที่ได้
        setCompanies(res.data);
      })
      .catch(err => {
        console.error('❌ ไม่สามารถดึงข้อมูลบริษัทได้:', err);
      });
  }, []);

  const handleDelete = (companyId) => {
    if (window.confirm('คุณต้องการลบบริษัทนี้หรือไม่?')) {
      axios.delete(`http://localhost:5000/api/admin/delete-company/${companyId}`)
        .then(res => {
          alert(res.data.message);
          setCompanies(companies.filter(company => company.company_id !== companyId));
        })
        .catch(err => {
          console.error('❌ เกิดข้อผิดพลาดในการลบบริษัท:', err);
          alert('ไม่สามารถลบได้');
        });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6 max-w-4xl mx-auto bg-white text-black rounded-lg shadow">
        <h1 className="text-2xl font-bold mb-4 text-indigo-700">บริษัทที่ได้รับการอนุมัติ</h1>

        {companies.length === 0 ? (
          <p>ไม่มีบริษัทที่ได้รับการอนุมัติ</p>
        ) : (
          <ul>
            {companies.map(company => (
              <li key={company.company_id} className="flex justify-between items-center mb-4">
                <div>
                  <p><strong>รหัสบริษัท:</strong> {company.company_id}</p>
                  <p><strong>ชื่อบริษัท:</strong> {company.company_name}</p>
                  <p><strong>ประเภทธุรกิจ:</strong> {company.business_type}</p>
                  <p><strong>ที่อยู่:</strong> {company.address}</p>
                  <p><strong>ลิงก์ Google Maps:</strong> {company.google_maps_link || 'ไม่มี'}</p>
                  <p><strong>อีเมลติดต่อ:</strong> {company.contact_email}</p>
                  <p><strong>ชื่อผู้ติดต่อ:</strong> {company.contact_name}</p>
                  <p><strong>เบอร์โทรติดต่อ:</strong> {company.phone_number}</p>
                  <p><strong>เว็บไซต์:</strong> {company.website || 'ไม่มี'}</p>
                  <p><strong>วันที่สร้าง:</strong> {company.created_date}</p>
                  <p><strong>อัพเดตล่าสุด:</strong> {company.last_updated}</p>
                  {company.company_logo && (
                    <div className="mt-2">
                      <strong>โลโก้บริษัท:</strong>
                      <img
                        src={`http://localhost:5000/uploads/${company.company_logo}`}
                        alt="Logo"
                        className="w-32 h-32 object-cover rounded"
                      />
                    </div>
                  )}
                </div>
                <div>
                  <button
                    onClick={() => handleDelete(company.company_id)}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                  >
                    ลบ
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default DashboardAdminCompanies;
