import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const SupervisorDetailPage = () => {
  const { id } = useParams();
  const [supervisor, setSupervisor] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/supervisor/${id}`)
      .then(res => setSupervisor(res.data))
      .catch(err => {
        console.error("❌ ไม่สามารถโหลดรายละเอียด:", err);
      });
  }, [id]);

  if (!supervisor) {
    return <div className="p-6 text-white">กำลังโหลดข้อมูล...</div>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="p-6 max-w-3xl mx-auto bg-white text-black rounded-lg shadow">
        <h2 className="text-2xl font-bold mb-4 text-purple-700">รายละเอียดอาจารย์นิเทศ</h2>
        <p><strong>ชื่อ:</strong> {supervisor.supervisor_name}</p>
        <p><strong>อีเมล:</strong> {supervisor.email}</p>
        <p><strong>เบอร์โทร:</strong> {supervisor.phone_number}</p>
        <p><strong>ภาควิชา:</strong> {supervisor.department}</p>
        <p><strong>คณะ:</strong> {supervisor.faculty}</p>
        <p><strong>ตำแหน่ง:</strong> {supervisor.position}</p>
        {supervisor.profile_image && (
          <div className="mt-4">
            <img
              src={`http://localhost:5000/uploads/${supervisor.profile_image}`}
              alt="รูปอาจารย์"
              className="w-32 h-32 object-cover rounded"
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default SupervisorDetailPage;
