// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      const go = (path, key) => { localStorage.setItem(key, id); navigate(path); };
      if (role === 'student') go('/dashboard/student', 'studentId');
      else if (role === 'company') go('/dashboard/company', 'companyId');
      else if (role === 'instructor') go('/dashboard/instructor', 'instructorId');
      else if (role === 'supervisor') go('/dashboard/supervisor', 'supervisorId');
      else if (role === 'admin') go('/admin/approvals', 'adminId');
      else return setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-sky-50 via-white to-blue-50 flex items-center justify-center p-4">
      {/* ลวดลายเบา ๆ ด้านหลัง */}
      <div className="pointer-events-none absolute inset-0">
        <div className="absolute -top-24 -right-16 w-72 h-72 rounded-full bg-sky-100 blur-3xl opacity-70"></div>
        <div className="absolute -bottom-24 -left-16 w-72 h-72 rounded-full bg-blue-100 blur-3xl opacity-70"></div>
      </div>

      {/* การ์ดฟอร์ม */}
      <div className="relative w-full max-w-md bg-white/90 backdrop-blur rounded-2xl shadow-xl border border-slate-100 p-8">
        <div className="text-center mb-6">
          <img
            src="/uploads/InternConnectLogo.png"
            alt="InternConnect"
            className="mx-auto w-16 h-16 object-contain"
          />
          <h1 className="mt-3 text-2xl font-semibold text-slate-800">InternConnect</h1>
          <p className="mt-1 text-slate-500 text-sm">ลงชื่อเข้าใช้เพื่อเริ่มต้นการฝึกงาน</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <label className="block">
            <span className="sr-only">Email</span>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-slate-200 bg-white focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400 transition"
            />
          </label>

          <label className="block">
            <span className="sr-only">Password</span>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-slate-200 bg-white focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400 transition"
            />
          </label>

          <div className="flex items-center justify-between text-sm">
            <span />
            <Link to="/forgot-password" className="text-sky-700 hover:underline">
              ลืมรหัสผ่าน?
            </Link>
          </div>

          <button
            type="submit"
            className="w-full py-3 rounded-xl font-semibold bg-gradient-to-r from-sky-600 to-blue-700 text-white hover:opacity-95 active:opacity-90 transition"
          >
            LOGIN
          </button>

          <p className="text-sm text-center text-slate-600">
            ยังไม่มีบัญชี?{' '}
            <Link to="/register" className="text-sky-700 hover:underline">
              สมัครสมาชิก
            </Link>
          </p>

          {message && (
            <p className="text-xs text-red-600 text-center">{message}</p>
          )}
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
