import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Header from '../components/Header';

const EvaluationCompanyForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const companyId = localStorage.getItem('companyId');

  const [student, setStudent] = useState(null);
  const [scores, setScores] = useState({});
  const [companyComment, setCompanyComment] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/student/${id}`)
      .then(res => setStudent(res.data))
      .catch(err => console.error('❌ โหลดนิสิตล้มเหลว:', err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setScores(prev => ({ ...prev, [name]: parseInt(value) }));
  };

  const calcTotalScore = () => {
    let total = 0;
    for (let i = 1; i <= 10; i++) total += parseInt(scores[`p${i}`]) || 0;
    for (let i = 1; i <= 10; i++) total += parseInt(scores[`w${i}`]) || 0;
    const weights = [2, 2, 4, 1];
    let penalty = 0;
    for (let i = 0; i < 4; i++) {
      penalty += (parseInt(scores[`absent_days${i}`]) || 0) * weights[i];
    }
    total += Math.max(0, 20 - penalty);
    return total;
  };

  const handleSubmit = async () => {
  // ✅ ตรวจว่าคะแนนบุคลิกภาพครบ (p1–p10)
  for (let i = 1; i <= 10; i++) {
    if (!scores[`p${i}`]) {
      alert(`❌ กรุณาให้คะแนนหัวข้อบุคลิกภาพ ข้อที่ ${i}`);
      return;
    }
  }

  // ✅ ตรวจว่าคะแนนการปฏิบัติงานครบ (w1–w10)
  for (let i = 1; i <= 10; i++) {
    if (!scores[`w${i}`]) {
      alert(`❌ กรุณาให้คะแนนหัวข้อการปฏิบัติงาน ข้อที่ ${i}`);
      return;
    }
  }

  // ✅ ตรวจว่ากรอกวันในหัวข้อเวลา (absent_days0–3)
  for (let i = 0; i <= 3; i++) {
    if (scores[`absent_days${i}`] === undefined || scores[`absent_days${i}`] === null || isNaN(scores[`absent_days${i}`])) {
      alert("❌ กรุณากรอกจำนวนวันในหัวข้อเวลาให้ครบทุกช่อง");
      return;
    }
  }

  const totalScore = calcTotalScore();

  try {
    await axios.post('http://localhost:5000/api/evaluation/submit', {
      student_id: id,
      company_score: totalScore,
      company_comment: companyComment,
      company_id: companyId,
      evaluation_date: new Date().toISOString().split('T')[0],
      role: 'company'
    });
    alert("✅ ส่งแบบประเมินสำเร็จ");
    navigate('/company/evaluation');
  } catch (err) {
    console.error("❌ บันทึกล้มเหลว:", err);
    alert("เกิดข้อผิดพลาด");
  }
};


  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="min-h-[calc(100vh-64px)] flex items-center justify-center p-6">
        <div className="bg-white text-black p-6 rounded-lg shadow-md max-w-4xl w-full">
          <h2 className="text-2xl font-bold text-orange-600 mb-4">📋 แบบประเมินนิสิตโดยสถานประกอบการ</h2>

          {renderSection("1. ประเมินบุคลิกภาพ (คะแนนรวม 50 คะแนน)", 'p', [
            "แต่งกายสะอาดถูกต้องตามระเบียบของมหาวิทยาลัย", "มีกิริยามารยาทเรียบร้อย มีสัมมาคาราวะ", "มีมนุษยสัมพันธ์ที่ดีกับคนทั่วไป", "มีความสามารถในการควบคุมอารมณ์ ไม่ฉุนเฉียว มีความอดทน อดกลั้น ร่าเริง แจ่มใส", "มีความว่องไว กระฉับกระเฉงในการทำงาน",
            "มีความรับผิดชอบต่องานในหน้าที่และงานที่ได้รับมอบหมาย", "มีการปรึกษาหารือและร่วมงานกับเพื่อนร่วมงานได้เป็นอย่างดี", "มีความสนใจที่จะพบหัวหน้าหน่วยงานหรือผู้ควบคุมประจำหน่วยงาน", "มีคุณลักษณะของผู้นำและผู้ตามที่ดี", "มาทำงานทันตามเวลาและไม่กลับก่อนเวลาที่กำหนด"
          ])}

          {renderSection("2. ประเมินการปฏิบัติงาน (คะแนนรวม 50 คะแนน)", 'w', [
            "ทำบันทึกที่ได้รับมอบหมายอย่างสม่ำเสมอและสะอาดเรียบร้อย", "เข้าใจจุดประสงค์ของงาน", "ทำการวางแผนการปฏิบัติงานที่ได้รับมอบหมายไว้ล่วงหน้า", "จัดทำปฏิทินการปฏิบัติงานหรือกำหนดเวลาการปฏิบัติงานอย่าง ชัดเจน",
            "เข้าใจขั้นตอนการปฏิบัติงาน", "ปฏิบัติงานได้เสร็จตามระยะเวลาที่กำหนด", "ทำบันทึกความก้าวหน้าของการปฏิบัติงานอย่างสม่ำเสมอ",
            "ผลการปฏิบัติงานเป็นไปตามจุดประสงค์ของงานที่ได้รับ มอบหมาย", "มีความสามารถในการแก้ปัญหาเฉพาะหน้า", "มีการประเมินและปรับปรุงแก้ไขข้อบกพร่องของตนเองอยู่เสมอ"
          ])}

          <h3 className="font-semibold mt-6 mb-3">3. ประเมินเวลาในการปฏิบัติการ (หักคะแนนจาก 20)</h3>
          {["ลาป่วย", "ลากิจ", "มาทำงานสาย", "ขาดงานโดยไม่ทราบสาเหตุ"].map((label, i) => (
            <div key={i} className="mb-2">
              {label}:
              <input
                type="number"
                name={`absent_days${i}`}
                min={0}
                step={1}
                placeholder="วัน"
                className="border p-1 w-20 ml-2 rounded"
                onChange={handleChange}
              />
              <span className="ml-2 text-gray-500">หักวันละ {[2, 2, 1, 4][i]} คะแนน</span>
            </div>
          ))}

          <h3 className="font-semibold mt-6 mb-3">ความคิดเห็นเพิ่มเติม</h3>
          <textarea
            className="border p-2 w-full rounded"
            rows={4}
            value={companyComment}
            onChange={(e) => setCompanyComment(e.target.value)}
          />

          <div className="mt-6 text-center">
            <button
              onClick={handleSubmit}
              className="bg-indigo-600 text-white px-6 py-2 rounded hover:bg-indigo-700"
            >
              ✅ ส่งแบบประเมิน
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EvaluationCompanyForm;
