import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale } from 'chart.js';
import Header from '../../components/Header';

ChartJS.register(BarElement, CategoryScale, LinearScale);

const DashboardAdminReports = () => {
  const [summary, setSummary] = useState({});
  const [topCompanies, setTopCompanies] = useState([]);
  const [topProvinces, setTopProvinces] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [summaryRes, companiesRes, provincesRes] = await Promise.all([
          axios.get('http://localhost:5000/api/reports/summary'),
          axios.get('http://localhost:5000/api/reports/top-companies'),
          axios.get('http://localhost:5000/api/reports/top-provinces'),
        ]);
        setSummary(summaryRes.data);
        setTopCompanies(companiesRes.data);
        setTopProvinces(provincesRes.data);
      } catch (err) {
        console.error('❌ ดึงข้อมูลล้มเหลว:', err);
      }
    };
    fetchData();
  }, []);

  const createBarData = (data, label) => ({
    labels: data.map((d) => d.label),
    datasets: [
      {
        label,
        data: data.map((d) => d.count),
        backgroundColor: '#facc15',
      },
    ],
  });

  const barOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false }
    },
    scales: {
      y: {
        ticks: {
          precision: 0 // ✅ ไม่เอาทศนิยม
        },
        beginAtZero: true
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6 grid gap-6">
        <h1 className="text-2xl font-bold text-gray-700">📊 สถิติระบบฝึกงาน</h1>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
          <div className="bg- rounded-lg p-4 shadow">
            <p className="text-4xl font-bold">{summary.studentCount || 0}</p>
            <p className="text-gray-600">นิสิตทั้งหมด</p>
          </div>
          <div className="bg-white rounded-lg p-4 shadow">
            <p className="text-4xl font-bold">{summary.companyCount || 0}</p>
            <p className="text-gray-600">สถานประกอบการ</p>
          </div>
          <div className="bg-green-100 rounded-lg p-4 shadow">
            <p className="text-4xl font-bold">{summary.passedCount || 0}</p>
            <p className="text-gray-600">ผ่าน</p>
          </div>
          <div className="bg-red-100 rounded-lg p-4 shadow">
            <p className="text-4xl font-bold">{summary.failedCount || 0}</p>
            <p className="text-gray-600">ไม่ผ่าน</p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-md font-bold mb-2">🏢 อันดับสถานประกอบการยอดนิยม</h2>
            <div style={{ width: '100%', maxWidth: '350px', height: '250px', margin: 'auto' }}>
              <Bar data={createBarData(topCompanies, 'จำนวนนิสิต')} options={barOptions} />
            </div>
          </div>
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-md font-bold mb-2">📍 จังหวัดที่มีสถานประกอบการมากที่สุด</h2>
            <div style={{ width: '100%', maxWidth: '350px', height: '250px', margin: 'auto' }}>
              <Bar data={createBarData(topProvinces, 'จำนวนบริษัท')} options={barOptions} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardAdminReports;
