import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardInstructorAssign = () => {
  const [students, setStudents] = useState([]);
  const [supervisors, setSupervisors] = useState([]);

  useEffect(() => {
    fetchStudents();
    fetchSupervisors();
  }, []);

  const fetchStudents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/instructor/confirmed-students');
      setStudents(res.data);
    } catch (err) {
      console.error("‚ùå ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
    }
  };

  const fetchSupervisors = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/instructor/all');
      setSupervisors(res.data);
    } catch (err) {
      console.error("‚ùå ‡∏î‡∏∂‡∏á‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠ supervisor ‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
    }
  };

  const handleSelectInstructor = async (studentId, instructorId) => {
    try {
      await axios.post('http://localhost:5000/api/instructor/assign-instructor', {
        student_id: studentId,
        instructor_id: instructorId,
      });
      console.log("‚úÖ ‡∏à‡∏±‡∏ö‡∏Ñ‡∏π‡πà supervisor ‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à");
      fetchStudents();
    } catch (err) {
      console.error("‚ùå ‡∏à‡∏±‡∏ö‡∏Ñ‡∏π‡πà supervisor ‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
    }
  };

  // ‚úÖ log ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≠‡∏ô students ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô)
  useEffect(() => {
    students.forEach((student) => {
      console.log(`üñºÔ∏è ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï ${student.student_id} =`, student.profile_image);
    });
  }, [students]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="p-6">
       
        <p className="mb-6">‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏à‡∏±‡∏ö‡∏Ñ‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏®‡πÅ‡∏•‡∏∞‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏ó‡∏µ‡πà‡∏î‡∏π‡πÅ‡∏•‡πÑ‡∏î‡πâ‡∏à‡∏≤‡∏Å‡∏´‡∏ô‡πâ‡∏≤‡∏ô‡∏µ‡πâ</p>

        {students.map((student) => (
          <div
            key={student.student_id}
            className="bg-white text-black p-4 rounded-lg shadow mb-4 flex flex-col md:flex-row justify-between items-center md:items-start gap-4"
          >
            {/* ‚úÖ ‡∏£‡∏π‡∏õ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï */}
            <img
              src={`http://localhost:5000/uploads/${student.profile_image || 'default-profile.png'}`}
              alt={student.student_name}
              className="round-profile"
            />


            {/* ‚úÖ ‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ô‡∏¥‡∏™‡∏¥‡∏ï */}
            <div className="flex-1">
              <p className="font-bold">{student.student_name}</p>
              <p>‡∏£‡∏´‡∏±‡∏™‡∏ô‡∏¥‡∏™‡∏¥‡∏ï: {student.student_id}</p>
              <p>‡∏≠‡∏≤‡∏¢‡∏∏: {student.age} | ‡πÄ‡∏û‡∏®: {student.gender}</p>
              <p>‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£: {student.phone_number}</p>
              <p>‡∏≠‡∏µ‡πÄ‡∏°‡∏•: {student.email}</p>
              <p>‡∏ö‡∏£‡∏¥‡∏©‡∏±‡∏ó‡∏ó‡∏µ‡πà‡πÑ‡∏õ‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô: {student.company_name}</p>
              <p>
                ‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î‡∏ó‡∏µ‡πà‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô:
                {(student.company_province && student.company_province.trim())
                  ? student.company_province
                  : "‡πÑ‡∏°‡πà‡∏ó‡∏£‡∏≤‡∏ö‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î"}
              </p>

              {student.supervisor_name && (
                <p className="text-green-700 font-semibold mt-2">
                  ‚úÖ ‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ó‡∏µ‡πà‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡πÑ‡∏ß‡πâ: {student.supervisor_name}
                </p>
              )}
            </div>

            {/* ‚úÖ Dropdown ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏® */}
            <select
              className="p-2 border rounded"
              value={student.supervisor_id || ''}
              onChange={(e) =>
                handleSelectInstructor(student.student_id, e.target.value)
              }
            >
              <option value="" disabled hidden>‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏®</option>
              {supervisors.map((sup) => (
                <option key={sup.supervisor_id} value={sup.supervisor_id}>
                  {sup.supervisor_name}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardInstructorAssign;
