// src/pages/ResetPassword.jsx
import { useState } from 'react';
import axios from 'axios';
import { useSearchParams, useNavigate } from 'react-router-dom';

const ResetPassword = () => {
  const [password, setPassword] = useState('');
  const [tokenValid, setTokenValid] = useState(true);
  const [searchParams] = useSearchParams();
  const token = searchParams.get('token');
  const navigate = useNavigate();
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/reset-password', { token, password });
      setMessage(res.data.message);
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Token ไม่ถูกต้องหรือหมดอายุ');
      setTokenValid(false);
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-2">
      {/* ซ้าย: โลโก้ใหญ่ พื้นฟ้าพาสเทล */}
      <div className="flex items-center justify-center bg-[#6EC7E2]">
        <img
          src="/uploads/InternConnectLogo2.png"
          alt="InternConnect"
          className="w-[340px] h-[340px] object-contain drop-shadow-lg"
        />
      </div>

      {/* ขวา: ฟอร์มพื้นขาว */}
      <div className="flex items-center justify-center bg-white p-8">
        <div className="w-full max-w-md">
          <h2 className="text-2xl font-bold text-center text-[#063D8C] mb-2">
            ตั้งรหัสผ่านใหม่
          </h2>
          <p className="text-sm text-center text-[#4691D3] mb-8">
            กรอกรหัสผ่านใหม่เพื่อรีเซ็ตบัญชีของคุณ
          </p>

          {tokenValid ? (
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="password"
                placeholder="รหัสผ่านใหม่"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-4 py-3 rounded-lg border bg-white
                           border-[#6EC7E2] placeholder-slate-400
                           focus:outline-none focus:ring-4 focus:ring-[#95FCF2] focus:border-[#225EC4]"
              />
              <button
                type="submit"
                className="w-full py-3 rounded-lg font-semibold
                           bg-[#063D8C] hover:bg-[#225EC4] text-white shadow-sm transition"
              >
                รีเซ็ตรหัสผ่าน
              </button>
            </form>
          ) : (
            <p className="text-red-600 text-center font-medium">{message}</p>
          )}

          {message && tokenValid && (
            <p className="text-green-600 text-center mt-4 font-medium">{message}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
