// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      const go = (path, key) => { localStorage.setItem(key, id); navigate(path); };
      if (role === 'student') go('/dashboard/student', 'studentId');
      else if (role === 'company') go('/dashboard/company', 'companyId');
      else if (role === 'instructor') go('/dashboard/instructor', 'instructorId');
      else if (role === 'supervisor') go('/dashboard/supervisor', 'supervisorId');
      else if (role === 'admin') go('/admin/approvals', 'adminId');
      else return setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-sky-100 via-sky-200 to-blue-200 p-4">
      <div className="relative w-full max-w-5xl bg-white rounded-3xl shadow-2xl overflow-hidden grid grid-cols-1 md:grid-cols-2">

        {/* Left panel (blue gradient + logo) */}
        <div className="relative hidden md:flex items-center justify-center bg-gradient-to-br from-blue-600 via-blue-500 to-sky-500 p-10">
          <div className="text-center text-white">
            <img
              src="/uploads/InternConnectLogo.png"
              alt="InternConnect"
              className="mx-auto mb-6 w-28 h-28 object-contain drop-shadow-xl"
            />
            <h1 className="text-3xl font-bold tracking-wide">InternConnect</h1>
            <p className="mt-2 text-white/80 text-sm">ระบบจัดการฝึกงานครบวงจร</p>
          </div>

          {/* Wave divider */}
          <svg
            className="absolute right-0 top-0 h-full w-24 text-white pointer-events-none"
            viewBox="0 0 100 100" preserveAspectRatio="none"
          >
            <path d="M0,0 C60,30 40,70 100,100 L100,0 Z" fill="currentColor" />
          </svg>
        </div>

        {/* Right panel (form) */}
        <div className="p-8 md:p-10 flex flex-col justify-center bg-white">
          <div className="md:hidden mb-6 text-center">
            <img
              src="/uploads/InternConnectLogo.png"
              alt="InternConnect"
              className="mx-auto w-16 h-16 object-contain"
            />
          </div>

          <h2 className="text-2xl font-bold text-blue-600 mb-6">Login</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-xl border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <div className="flex items-center justify-between text-sm">
              <span />
              <Link to="/forgot-password" className="text-blue-600 hover:underline">
                ลืมรหัสผ่าน?
              </Link>
            </div>
            <button
              type="submit"
              className="w-full py-3 rounded-xl font-semibold bg-gradient-to-r from-blue-600 to-sky-500 text-white hover:opacity-90 transition"
            >
              LOGIN
            </button>
            <p className="text-sm text-center">
              ยังไม่มีบัญชี?{' '}
              <Link to="/register" className="text-blue-600 hover:underline">
                สมัครสมาชิก
              </Link>
            </p>
            {message && <p className="text-xs text-red-600 text-center">{message}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
