import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardInstructor = () => {
  const [report, setReport] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/instructor/internship-report')
      .then(res => setReport(res.data))
      .catch(err => console.error("❌ โหลดรายงานล้มเหลว:", err));
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-6">จำนวนนิสิตสถานประกอบการรับเข้าฝึกงาน</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {report.map((company, index) => (
            <div key={index} className="bg-gray-100 text-black rounded-xl p-4 shadow-md">
              <h2 className="text-xl font-semibold mb-2">บริษัท {company.company_name}</h2>
              <table className="w-full text-sm">
                <thead>
                  <tr className="text-left font-bold border-b border-gray-300">
                    <th>ตำแหน่งงาน</th>
                    <th>จำนวนนิสิต</th>
                  </tr>
                </thead>
                <tbody>
                  {company.positions.map((pos, i) => (
                    <tr key={i}>
                      <td>{pos.position}</td>
                      <td>{pos.student_count}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DashboardInstructor;
