// src/pages/StudentDetailPage.jsx
import React, { useEffect, useState, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

/* ---------- helpers ---------- */
const buildImageUrl = (val) => {
  if (!val) return null;
  if (/^https?:\/\//i.test(val)) return val;
  const file = String(val).split(/[/\\]/).pop();
  return `http://localhost:5000/uploads/${encodeURIComponent(file)}`;
};

const formatDate = (v) => (v ? String(v).split('T')[0] : '-');

/* avatar fallback */
const PlaceholderAvatar = ({ name }) => {
  const initials = useMemo(
    () =>
      (name || '?')
        .split(' ')
        .map((w) => w[0]?.toUpperCase())
        .slice(0, 2)
        .join(''),
    [name]
  );
  return (
    <div
      className="w-28 h-28 md:w-32 md:h-32 rounded-full flex items-center justify-center text-white text-2xl font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
      aria-label="avatar"
    >
      {initials || 'S'}
    </div>
  );
};

const Chip = ({ children, className = '' }) => (
  <span
    className={`inline-flex items-center px-3 py-1 rounded-full border text-xs font-semibold ${className}`}
  >
    {children}
  </span>
);

const Field = ({ label, value }) => (
  <div>
    <div className="text-xs text-[#465d71]">{label}</div>
    <div className="font-semibold text-[#130347]">{value || '-'}</div>
  </div>
);

/* ---------- page ---------- */
const StudentDetailPage = () => {
  const { id } = useParams();
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/student/${id}`);
        setStudent(res.data || {});
      } catch (e) {
        console.error('❌ ดึงข้อมูลนิสิตผิดพลาด:', e);
        setStudent(null);
      } finally {
        setLoading(false);
      }
    })();
  }, [id]);

  if (loading) {
    return (
      <div className="min-h-screen bg-[#9AE5F2]">
        <Header />
        <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-10">
          <div className="rounded-2xl border border-[#E6F0FF] bg-white p-8 shadow-sm animate-pulse">
            <div className="h-28 bg-gray-200 rounded-xl mb-10" />
            <div className="h-6 bg-gray-200 rounded w-64 mb-2" />
            <div className="h-4 bg-gray-200 rounded w-40" />
          </div>
        </div>
      </div>
    );
  }

  if (!student) {
    return (
      <div className="min-h-screen bg-[#9AE5F2]">
        <Header />
        <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-10">
          <div className="rounded-2xl border border-[#E6F0FF] bg-white p-10 shadow-sm text-center text-[#465d71]">
            ไม่พบนิสิตที่ระบุ
          </div>
        </div>
      </div>
    );
  }

  const imgSrc = buildImageUrl(student.profile_image);

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-8">
        {/* HERO */}
        <section className="bg-white border border-[#E6F0FF] rounded-2xl shadow-sm overflow-hidden">
          <div className="h-28 md:h-36 bg-gradient-to-r from-[#6EC7E2] via-[#4691D3] to-[#225EC4]" />
          <div className="px-6 pb-6 -mt-14 md:-mt-16">
            <div className="flex flex-col items-center text-center">
              <div className="ring-4 ring-white rounded-full">
                {imgSrc ? (
                  <img
                    src={imgSrc}
                    alt="profile"
                    className="w-28 h-28 md:w-32 md:h-32 rounded-full object-cover border border-[#E6F0FF] bg-white"
                    onError={(e) => {
                      e.currentTarget.style.display = 'none';
                    }}
                  />
                ) : (
                  <PlaceholderAvatar name={student.student_name} />
                )}
              </div>

              <h1 className="mt-3 text-xl md:text-2xl font-extrabold text-[#130347]">
                {student.student_name || '-'}
              </h1>
              <div className="text-sm text-[#465d71]">
                รหัสนิสิต: {student.student_id || '-'}
              </div>

              {/* chips */}
              <div className="mt-3 flex flex-wrap justify-center gap-2">
                {student.gender ? (
                  <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                    เพศ {student.gender}
                  </Chip>
                ) : null}
                {student.year_level ? (
                  <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                    ชั้นปี {student.year_level}
                  </Chip>
                ) : null}
                {student.gpa ? (
                  <Chip className="bg-emerald-50 border-emerald-200 text-emerald-700">
                    GPA {student.gpa}
                  </Chip>
                ) : null}
                {student.age ? (
                  <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                    อายุ {student.age}
                  </Chip>
                ) : null}
              </div>

              {/* actions */}
              <div className="mt-4 flex flex-wrap justify-center gap-2">
                {student.phone_number ? (
                  <a
                    href={`tel:${student.phone_number}`}
                    className="rounded-full bg-[#225EC4] hover:bg-[#1b55b5] text-white text-sm font-semibold px-5 py-2 shadow-sm"
                  >
                    📞 โทรหา
                  </a>
                ) : null}
                {student.email ? (
                  <a
                    href={`mailto:${student.email}`}
                    className="rounded-full bg-[#6EC7E2] hover:bg-[#55b7d4] text-[#130347] text-sm font-semibold px-5 py-2 shadow-sm"
                  >
                    ✉️ ส่งอีเมล
                  </a>
                ) : null}
              </div>
            </div>
          </div>
        </section>

        {/* DETAILS GRID */}
        <section className="mt-6 grid grid-cols-1 lg:grid-cols-3 gap-4">
          {/* การศึกษา / ติดต่อ */}
          <div className="lg:col-span-2 bg-white border border-[#E6F0FF] rounded-2xl p-6 shadow-sm">
            <h2 className="text-md font-bold text-[#130347] mb-3">ข้อมูลส่วนตัว & การศึกษา</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Field label="อีเมล" value={student.email} />
              <Field label="เบอร์โทร" value={student.phone_number} />
              <Field label="มหาวิทยาลัย" value={student.university} />
              <Field label="คณะ" value={student.faculty} />
              <Field label="สาขาวิชา" value={student.major} />
              <Field label="วันเกิด" value={formatDate(student.birth_date)} />
            </div>
          </div>

          {/* ช่วงฝึกงาน */}
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-6 shadow-sm">
            <h2 className="text-md font-bold text-[#130347] mb-3">ข้อมูลการฝึกงาน</h2>
            <div className="space-y-3">
              <Field label="บริษัทที่ฝึก" value={student.company_name} />
              <Field label="จังหวัด" value={student.province} />
              <Field label="วันที่เริ่มฝึก" value={formatDate(student.intern_start_date)} />
              <Field label="วันที่สิ้นสุด" value={formatDate(student.intern_end_date)} />
            </div>
          </div>

          {/* ทักษะพิเศษ */}
          <div className="lg:col-span-3 bg-white border border-[#E6F0FF] rounded-2xl p-6 shadow-sm">
            <h2 className="text-md font-bold text-[#130347] mb-3">ทักษะพิเศษ</h2>
            <div className="text-sm text-[#130347] leading-7 whitespace-pre-line">
              {student.special_skills || '-'}
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default StudentDetailPage;
