// src/pages/dashboard/DashboardCompanyStatus.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';

const DashboardCompanyStatus = () => {
  const [applications, setApplications] = useState([]);
  const navigate = useNavigate();
  const company_id = localStorage.getItem('companyId');

  useEffect(() => {
    if (!company_id) return;
    axios
      .get(`http://localhost:5000/api/job_posting/applications/${company_id}`)
      .then((res) => setApplications(Array.isArray(res.data) ? res.data : []))
      .catch((err) => console.error('‚ùå ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err));
  }, [company_id]);

  const handleStatusChange = async (application_id, newStatus) => {
    try {
      await axios.put(
        `http://localhost:5000/api/job_posting/application/status/${application_id}`,
        { status: newStatus }
      );
      setApplications((apps) =>
        apps.map((a) =>
          a.application_id === application_id ? { ...a, status: newStatus } : a
        )
      );
    } catch (err) {
      console.error('‚ùå ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à:', err);
    }
  };

  const statusBadge = (s) => {
    if (s === 1)
      return 'inline-flex items-center px-2.5 py-1 rounded-full text-sm font-semibold bg-emerald-50 text-emerald-700 border border-emerald-200';
    if (s === 2)
      return 'inline-flex items-center px-2.5 py-1 rounded-full text-sm font-semibold bg-rose-50 text-rose-700 border border-rose-200';
    return 'inline-flex items-center px-2.5 py-1 rounded-full text-sm font-semibold bg-amber-50 text-amber-700 border border-amber-200';
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] text-[#063D8C]">
      <Header />

      <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-8">
        {/* ‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á */}
        <div className="mb-4">
          <h1 className="text-2xl font-extrabold text-[#130347]">
            üóÇÔ∏è ‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏Å‡∏≤‡∏£‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏Ç‡∏≠‡∏á‡∏ô‡∏¥‡∏™‡∏¥‡∏ï
          </h1>
          <p className="text-sm text-[#465d71]">
            ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏Ç‡∏≠‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏ù‡∏∂‡∏Å‡∏á‡∏≤‡∏ô‡∏Ç‡∏≠‡∏á‡∏ô‡∏¥‡∏™‡∏¥‡∏ï ‡∏õ‡∏£‡∏±‡∏ö‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞ ‡πÅ‡∏•‡∏∞‡πÄ‡∏õ‡∏¥‡∏î‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö
          </p>
        </div>

        {/* ‡∏Å‡∏£‡∏¥‡∏î‡∏Å‡∏≤‡∏£‡πå‡∏î */}
        {applications.length === 0 ? (
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-10 text-center text-[#465d71]">
            ‚õ≥ ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏™‡∏°‡∏±‡∏Ñ‡∏£
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {applications.map((app) => (
              <div
                key={app.application_id}
                className="relative bg-white rounded-2xl border border-[#E6F0FF] p-5 shadow-sm hover:shadow-md transition"
              >
                {/* ‡∏õ‡∏∏‡πà‡∏°‡∏î‡πâ‡∏≤‡∏ô‡∏Ç‡∏ß‡∏≤‡∏ö‡∏ô */}
                <div className="absolute right-4 top-4 flex gap-2">
                  <a
                    href={`http://localhost:5000/uploads/resumes/${app.resume_file}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center gap-2 rounded-full bg-emerald-500 hover:bg-emerald-600 text-white text-sm font-semibold px-4 py-2 shadow-sm"
                    title="‡∏î‡∏π Resume"
                  >
                    üìé ‡∏î‡∏π Resume
                  </a>
                  <button
                    onClick={() => navigate(`/student-detail/${app.student_id}`)}
                    className="inline-flex items-center gap-2 rounded-full bg-indigo-500 hover:bg-indigo-600 text-white text-sm font-semibold px-4 py-2 shadow-sm"
                    title="‡∏î‡∏π‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ï‡∏¥‡∏°"
                  >
                    üëÅÔ∏è‚Äçüó®Ô∏è ‡∏î‡∏π‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ï‡∏¥‡∏°
                  </button>
                </div>

                {/* ‡∏´‡∏±‡∏ß‡∏Å‡∏≤‡∏£‡πå‡∏î */}
                <div className="pr-40">
                  <div className="flex items-center justify-between">
                    <h3 className="text-lg font-bold text-[#130347]">
                      {app.student_name}
                    </h3>
                    <span className={statusBadge(app.status)}>
                      {app.status === 1
                        ? '‡∏£‡∏±‡∏ö'
                        : app.status === 2
                        ? '‡πÑ‡∏°‡πà‡∏£‡∏±‡∏ö'
                        : '‡∏≠‡∏¢‡∏π‡πà‡∏£‡∏∞‡∏´‡∏ß‡πà‡∏≤‡∏á‡∏î‡∏≥‡πÄ‡∏ô‡∏¥‡∏ô‡∏Å‡∏≤‡∏£'}
                    </span>
                  </div>
                  <p className="text-xs text-[#465d71]">
                    ‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á‡∏ó‡∏µ‡πà‡∏™‡∏°‡∏±‡∏Ñ‡∏£: {app.position || '-'}
                  </p>
                </div>

                {/* ‡πÄ‡∏ô‡∏∑‡πâ‡∏≠‡∏´‡∏≤ */}
                <div className="mt-3 text-sm text-[#130347] space-y-1 pr-2">
                  <p>
                    <strong>‡∏£‡∏´‡∏±‡∏™‡∏ô‡∏¥‡∏™‡∏¥‡∏ï:</strong> {app.student_id}
                  </p>
                  <p>
                    <strong>‡∏≠‡∏µ‡πÄ‡∏°‡∏•:</strong> {app.email}
                  </p>
                  <p>
                    <strong>‡πÄ‡∏ö‡∏≠‡∏£‡πå:</strong> {app.phone_number}
                  </p>
                  <p>
                    <strong>‡∏Ñ‡∏ì‡∏∞:</strong> {app.faculty}
                  </p>
                  <p>
