import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardSupervisorProfile = () => {
  const [form, setForm] = useState({
    supervisor_id: '',
    supervisor_name: '',
    email: '',
    phone_number: '',
    department: '',
    faculty: '',
    position: '',
    profile_image: ''
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const supervisorId = localStorage.getItem('supervisorId');

  useEffect(() => {
    if (supervisorId) {
      axios.get(`http://localhost:5000/api/supervisor/${supervisorId}`)
        .then((res) => {
          const data = res.data;
          setForm({
            supervisor_id: data.supervisor_id || '',
            supervisor_name: data.supervisor_name || '',
            email: data.email || '',
            phone_number: data.phone_number || '',
            department: data.department || '',
            faculty: data.faculty || '',
            position: data.position || '',
            profile_image: data.profile_image || ''
          });
        })
        .catch((err) => {
          console.error('‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏®‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        });
    }
  }, [supervisorId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let profileImageFilename = form.profile_image;

    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const res = await axios.post('http://localhost:5000/api/upload/profile-image', formData);
        profileImageFilename = res.data.filename;
      } catch (err) {
        console.error('‚ùå ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ');
        return;
      }
    }

    const updatedForm = { ...form, profile_image: profileImageFilename };

    axios.put(`http://localhost:5000/api/supervisor/${supervisorId}`, updatedForm)
      .then(() => {
        localStorage.setItem('profile_image', profileImageFilename);
        alert('‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß');
      })
      .catch((err) => {
        console.error('‚ùå ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å');
      });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white font-sans">
      <Header />
      <div className="flex justify-center items-center p-6 min-h-screen">

        <h2 className="text-3xl font-bold mb-4">üë§ ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏®</h2>
        <p className="mb-6">‡∏î‡∏π‡πÅ‡∏•‡∏∞‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡πà‡∏ß‡∏ô‡∏ï‡∏±‡∏ß‡∏Ç‡∏≠‡∏á‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå‡∏ô‡∏¥‡πÄ‡∏ó‡∏®</p>

        <form onSubmit={handleSubmit} className="bg-white text-black p-6 rounded-lg shadow-md max-w-xl">
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏£‡∏´‡∏±‡∏™‡∏≠‡∏≤‡∏à‡∏≤‡∏£‡∏¢‡πå</label>
            <input name="supervisor_id" value={form.supervisor_id} disabled className="w-full border rounded px-3 py-2 bg-gray-100" />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏ä‡∏∑‡πà‡∏≠</label>
            <input name="supervisor_name" value={form.supervisor_name} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</label>
            <input type="email" name="email" value={form.email} onChange={handleChange} className="w-full border rounded px-3 py-2" required />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£</label>
            <input name="phone_number" value={form.phone_number} onChange={handleChange} className="w-full border rounded px-3 py-2" />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏†‡∏≤‡∏Ñ‡∏ß‡∏¥‡∏ä‡∏≤</label>
            <input name="department" value={form.department} onChange={handleChange} className="w-full border rounded px-3 py-2" />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏Ñ‡∏ì‡∏∞</label>
            <input name="faculty" value={form.faculty} onChange={handleChange} className="w-full border rounded px-3 py-2" />
          </div>
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á</label>
            <input name="position" value={form.position} onChange={handleChange} className="w-full border rounded px-3 py-2" />
          </div>

          {/* ‚úÖ ‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå */}
          <div className="mb-4">
            <label className="block mb-1 font-semibold">‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå</label>
            <input type="file" accept="image/*" onChange={handleImageUpload} className="w-full" />
          </div>

          {/* ‚úÖ ‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå */}
          {form.profile_image && (
            <div className="flex justify-center mb-4">
              <img
                src={`http://localhost:5000/uploads/${form.profile_image}`}
                alt="‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå"
                className="square-profile"
              />
            </div>
          )}

          <button type="submit" className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700">üíæ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å</button>
        </form>
      </div>
    </div>
  );
};

export default DashboardSupervisorProfile;
