// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      switch (role) {
        case 'student':
          localStorage.setItem('studentId', id);
          navigate('/dashboard/student'); break;
        case 'company':
          localStorage.setItem('companyId', id);
          navigate('/dashboard/company'); break;
        case 'instructor':
          localStorage.setItem('instructorId', id);
          navigate('/dashboard/instructor'); break;
        case 'supervisor':
          localStorage.setItem('supervisorId', id);
          navigate('/dashboard/supervisor'); break;
        case 'admin':
          localStorage.setItem('adminId', id);
          navigate('/admin/approvals'); break;
        default:
          setMessage('บทบาทผู้ใช้ไม่ถูกต้อง'); return;
      }

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-indigo-500 via-purple-500 to-fuchsia-500 flex items-center justify-center p-4">
      {/* Card */}
      <div className="w-full max-w-5xl bg-white/80 backdrop-blur rounded-3xl shadow-2xl overflow-hidden grid grid-cols-1 md:grid-cols-2">
        {/* Left gradient panel */}
        <div className="relative hidden md:flex items-center justify-center p-10 bg-gradient-to-br from-indigo-600 via-purple-600 to-fuchsia-600">
          <div className="absolute -right-10 top-0 bottom-0 w-24 bg-white rounded-l-full opacity-90"></div>

          <div className="relative z-10 text-center text-white">
            <img
              src="/internconnect-logo.png"
              alt="InternConnect"
              className="mx-auto mb-6 w-28 h-28 object-contain drop-shadow-xl"
            />
            <h1 className="text-3xl font-bold tracking-wide">InternConnect</h1>
            <p className="mt-3 text-white/80 text-sm">
              เชื่อมต่อ นิสิต • บริษัท • อาจารย์ • ผู้ควบคุมงาน
            </p>

            <Link
              to="/register"
              className="inline-block mt-8 px-8 py-3 rounded-full bg-white/30 hover:bg-white/40 transition font-semibold backdrop-blur"
            >
              GET STARTED
            </Link>
          </div>
        </div>

        {/* Right form panel */}
        <div className="p-8 md:p-10">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Login</h2>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={form.email}
                onChange={handleChange}
                required
                className="w-full px-4 py-3 rounded-xl border border-gray-200 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-400"
              />
            </div>

            <div>
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={form.password}
                onChange={handleChange}
                required
                className="w-full px-4 py-3 rounded-xl border border-gray-200 bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-400"
              />
            </div>

            <div className="flex items-center justify-between text-sm">
              <span />
              <Link to="/forgot-password" className="text-indigo-600 hover:underline">
                ลืมรหัสผ่าน?
              </Link>
            </div>

            <button
              type="submit"
              className="w-full py-3 rounded-xl font-semibold bg-gradient-to-r from-indigo-500 to-fuchsia-500 text-white hover:opacity-90 transition"
            >
              LOGIN
            </button>

            <p className="text-sm text-center">
              ยังไม่มีบัญชี?{' '}
              <Link to="/register" className="text-indigo-600 hover:underline">
                สมัครสมาชิก
              </Link>
            </p>

            {message && (
              <p className="text-xs text-red-600 text-center">{message}</p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
