import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardSupervisorProfile = () => {
  const [form, setForm] = useState({
    supervisor_id: '',
    supervisor_name: '',
    email: '',
    phone_number: '',
    department: '',
    faculty: '',
    position: '',
    profile_image: ''
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const supervisorId = localStorage.getItem('supervisorId');

  useEffect(() => {
    if (supervisorId) {
      axios.get(`http://localhost:5000/api/supervisor/${supervisorId}`)
        .then((res) => {
          const data = res.data;
          setForm({
            supervisor_id: data.supervisor_id || '',
            supervisor_name: data.supervisor_name || '',
            email: data.email || '',
            phone_number: data.phone_number || '',
            department: data.department || '',
            faculty: data.faculty || '',
            position: data.position || '',
            profile_image: data.profile_image || ''
          });
        })
        .catch((err) => {
          console.error('❌ โหลดข้อมูลอาจารย์นิเทศล้มเหลว:', err);
        });
    }
  }, [supervisorId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    let profileImageFilename = form.profile_image;

    if (selectedFile) {
      const formData = new FormData();
      formData.append('image', selectedFile);

      try {
        const res = await axios.post('http://localhost:5000/api/upload/profile-image', formData);
        profileImageFilename = res.data.filename;
      } catch (err) {
        console.error('❌ อัปโหลดรูปโปรไฟล์ล้มเหลว:', err);
        alert('เกิดข้อผิดพลาดในการอัปโหลดรูป');
        return;
      }
    }

    const updatedForm = { ...form, profile_image: profileImageFilename };

    axios.put(`http://localhost:5000/api/supervisor/${supervisorId}`, updatedForm)
      .then(() => {
        localStorage.setItem('profile_image', profileImageFilename);
        alert('✅ บันทึกข้อมูลเรียบร้อยแล้ว');
      })
      .catch((err) => {
        console.error('❌ บันทึกข้อมูลล้มเหลว:', err);
        alert('เกิดข้อผิดพลาดในการบันทึก');
      });
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] font-sans">
      <Header />
      <div className="min-h-[calc(100vh-64px)] flex items-center justify-center p-8">
        <form
          onSubmit={handleSubmit}
          className="bg-white text-black p-8 rounded-2xl shadow-lg w-full max-w-2xl space-y-5"
        >
          <div className="text-center">
            <h2 className="text-3xl font-bold text-[#130347]">👤 โปรไฟล์อาจารย์นิเทศ</h2>
            <p className="text-[#465d71]">ดูและแก้ไขข้อมูลส่วนตัวของอาจารย์นิเทศ</p>
          </div>

          {renderInput('รหัสอาจารย์', 'supervisor_id', form.supervisor_id, handleChange, true)}
          {renderInput('ชื่อ', 'supervisor_name', form.supervisor_name, handleChange)}
          {renderInput('อีเมล', 'email', form.email, handleChange, false, 'email')}
          {renderInput('เบอร์โทร', 'phone_number', form.phone_number, handleChange)}
          {renderInput('ภาควิชา', 'department', form.department, handleChange)}
          {renderInput('คณะ', 'faculty', form.faculty, handleChange)}
          {renderInput('ตำแหน่ง', 'position', form.position, handleChange)}

          <div>
            <label className="block mb-1 font-semibold">อัปโหลดรูปโปรไฟล์</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="w-full border border-gray-300 rounded px-3 py-2"
            />
          </div>

          {form.profile_image && (
            <div className="flex justify-center">
              <img
                src={`http://localhost:5000/uploads/${form.profile_image}`}
                alt="รูปโปรไฟล์"
                className="w-32 h-32 rounded-full object-cover ring-4 ring-[#6EC7E2]"
              />
            </div>
          )}

          <div className="text-center">
            <button
              type="submit"
              className="bg-[#225EC4] hover:bg-[#063D8C] text-white px-6 py-2 rounded-lg shadow"
            >
              💾 บันทึก
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// ฟังก์ชันช่วยสร้าง input
const renderInput = (label, name, value, onChange, disabled = false, type = 'text') => (
  <div>
    <label className="block mb-1 font-semibold">{label}</label>
    <input
      type={type}
      name={name}
      value={value}
      onChange={onChange}
      disabled={disabled}
      className={`w-full border rounded px-3 py-2 ${disabled ? 'bg-gray-100' : 'bg-white'} focus:outline-none focus:ring-2 focus:ring-[#6EC7E2]`}
      required={!disabled}
    />
  </div>
);

export default DashboardSupervisorProfile;
