// src/pages/StudentDetailPage.jsx
import React, { useEffect, useState, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

/* Helpers */
const formatDate = (v) => (v ? v.split('T')[0] : '-');
const buildImageUrl = (val) => {
  if (!val) return null;
  if (/^https?:\/\//i.test(val)) return val;
  const file = String(val).split(/[/\\]/).pop();
  return `http://localhost:5000/uploads/${encodeURIComponent(file)}`;
};

const PlaceholderAvatar = ({ name }) => {
  const initials = (name || '?')
    .split(' ')
    .map((w) => w[0]?.toUpperCase())
    .slice(0, 2)
    .join('');
  return (
    <div
      className="w-24 h-24 md:w-28 md:h-28 rounded-2xl flex items-center justify-center text-white font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
    >
      {initials || 'S'}
    </div>
  );
};

const Field = ({ label, children }) => (
  <div>
    <div className="text-xs text-[#465d71]">{label}</div>
    <div className="font-semibold text-[#130347]">{children || '-'}</div>
  </div>
);

const Chip = ({ children, className = '' }) => (
  <span
    className={`inline-flex items-center px-2.5 py-1 rounded-full border text-xs font-semibold ${className}`}
  >
    {children}
  </span>
);

const StudentDetailPage = () => {
  const { id } = useParams();
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/student/${id}`);
        setStudent(res.data || null);
      } catch (e) {
        console.error('❌ ดึงข้อมูลนิสิตผิดพลาด:', e);
        setStudent(null);
      } finally {
        setLoading(false);
      }
    })();
  }, [id]);

  const avatarSrc = useMemo(
    () => buildImageUrl(student?.profile_image),
    [student?.profile_image]
  );

  return (
    <div className="min-h-screen bg-[#9AE5F2] text-[#063D8C]">
      <Header />

      <div className="w-full max-w-screen-lg mx-auto px-4 lg:px-8 py-8">
        {/* หัวเรื่อง */}
        <div className="mb-4">
          <h1 className="text-2xl font-extrabold text-[#130347]">👤 รายละเอียดนิสิต</h1>
          <p className="text-sm text-[#465d71]">ข้อมูลส่วนตัว ช่องทางติดต่อ และช่วงเวลาฝึกงาน</p>
        </div>

        {/* สถานะโหลด */}
        {loading ? (
          <div className="rounded-2xl border border-[#E6F0FF] bg-white p-6 shadow-sm animate-pulse">
            <div className="flex items-start gap-4">
              <div className="w-28 h-28 rounded-2xl bg-gray-200" />
              <div className="flex-1 space-y-3">
                <div className="h-4 w-56 bg-gray-200 rounded" />
                <div className="h-3 w-80 bg-gray-200 rounded" />
                <div className="h-3 w-64 bg-gray-200 rounded" />
                <div className="h-3 w-72 bg-gray-200 rounded" />
              </div>
            </div>
          </div>
        ) : !student ? (
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-10 text-center text-[#465d71]">
            ไม่พบข้อมูลนิสิต
          </div>
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* ซ้าย: การ์ดโปรไฟล์ + ชิพสรุป */}
            <aside className="lg:col-span-1">
              <div className="bg-white rounded-2xl border border-[#E6F0FF] p-6 shadow-sm">
                <div className="flex items-start gap-4">
                  {avatarSrc ? (
                    <img
                      src={avatarSrc}
                      alt="profile"
                      className="w-24 h-24 md:w-28 md:h-28 rounded-2xl object-cover border border-[#E6F0FF]"
                      onError={(e) => {
                        e.currentTarget.style.display = 'none';
                      }}
                    />
                  ) : (
                    <PlaceholderAvatar name={student.student_name} />
                  )}
                  <div className="flex-1">
                    <div className="text-lg font-bold text-[#130347]">{student.student_name || '-'}</div>
                    <div className="text-xs text-[#465d71]">รหัสนิสิต: {student.student_id || '-'}</div>

                    <div className="mt-3 flex flex-wrap gap-2">
                      {student.gender ? (
                        <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                          เพศ {student.gender}
                        </Chip>
                      ) : null}
                      {student.year_level ? (
                        <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                          ชั้นปี {student.year_level}
                        </Chip>
                      ) : null}
                      {student.gpa ? (
                        <Chip className="bg-emerald-50 border-emerald-200 text-emerald-700">
                          GPA {student.gpa}
                        </Chip>
                      ) : null}
                      {student.age ? (
                        <Chip className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                          อายุ {student.age}
                        </Chip>
                      ) : null}
                    </div>
                  </div>
                </div>

                <div className="mt-4 space-y-1 text-sm text-[#130347]">
                  <div>📧 {student.email || '-'}</div>
                  <div>📞 {student.phone_number || '-'}</div>
                </div>
              </div>
            </aside>

            {/* ขวา: รายละเอียด */}
            <section className="lg:col-span-2 space-y-4">
              {/* การศึกษา */}
              <div className="bg-white rounded-2xl border border-[#E6F0FF] p-6 shadow-sm">
                <h3 className="font-bold text-[#130347] mb-3">ข้อมูลการศึกษา</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <Field label="สาขาวิชา">{student.major}</Field>
                  <Field label="คณะ">{student.faculty}</Field>
                  <Field label="มหาวิทยาลัย">{student.university}</Field>
                  <Field label="วันเกิด">{formatDate(student.birth_date)}</Field>
                </div>
              </div>

              {/* ช่วงฝึกงาน */}
              <div className="bg-white rounded-2xl border border-[#E6F0FF] p-6 shadow-sm">
                <h3 className="font-bold text-[#130347] mb-3">ช่วงเวลาฝึกงาน</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <Field label="วันที่เริ่มฝึกงาน">{formatDate(student.intern_start_date)}</Field>
                  <Field label="วันที่สิ้นสุดฝึกงาน">{formatDate(student.intern_end_date)}</Field>
                </div>
              </div>

              {/* ทักษะพิเศษ */}
              <div className="bg-white rounded-2xl border border-[#E6F0FF] p-6 shadow-sm">
                <h3 className="font-bold text-[#130347] mb-2">ทักษะพิเศษ</h3>
                <p className="text-sm text-[#130347] whitespace-pre-line">
                  {student.special_skills || '-'}
                </p>
              </div>
            </section>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentDetailPage;
