import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';

const DashboardCompany = () => {
  const [jobPosts, setJobPosts] = useState([]);
  const [editingJob, setEditingJob] = useState(null);
  const [formData, setFormData] = useState({});
  const navigate = useNavigate();

  const fetchJobs = async () => {
    const company_id = localStorage.getItem('companyId');
    if (!company_id) {
      alert("‡πÑ‡∏°‡πà‡∏û‡∏ö companyId ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö‡πÉ‡∏´‡∏°‡πà");
      return;
    }
    try {
      const res = await axios.get(`http://localhost:5000/api/job_posting/company/${company_id}`);
      setJobPosts(res.data);
    } catch (err) {
      console.error("‚ùå ‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô:", err);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const handleDelete = async (jobId) => {
    if (!window.confirm('‡∏Ñ‡∏∏‡∏ì‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏ô‡∏µ‡πâ‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/job_posting/${jobId}`);
      fetchJobs();
    } catch (err) {
      console.error('‚ùå ‡∏•‡∏ö‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏ö');
    }
  };

  const handleEditClick = (job) => {
    setEditingJob(job);
    setFormData(job);
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/job_posting/${editingJob.job_posting_id}`, formData);
      setEditingJob(null);
      fetchJobs();
    } catch (err) {
      console.error('‚ùå ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6">
        

        <div className="bg-white text-black p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">üì¢ ‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô‡∏Ç‡∏≠‡∏á‡∏Ñ‡∏∏‡∏ì</h2>
          {jobPosts.length === 0 ? (
            <p>üîé ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô ‡∏´‡∏£‡∏∑‡∏≠‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î...</p>
          ) : (
            jobPosts.map((job) => (
              <div key={job.job_posting_id} className="mb-6 p-4 bg-gray-100 rounded-md shadow">
                <p className="font-bold text-lg">üìÑ ‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</p>
                <p><strong>‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á :</strong> {job.position}</p>
                <p><strong>‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î :</strong> {job.job_description}</p>
                <p><strong>‡∏Ñ‡∏∏‡∏ì‡∏™‡∏°‡∏ö‡∏±‡∏ï‡∏¥ :</strong> {job.requirements}</p>
                <p><strong>‡∏Ñ‡πà‡∏≤‡∏ï‡∏≠‡∏ö‡πÅ‡∏ó‡∏ô :</strong> {job.compensation} ‡∏ö‡∏≤‡∏ó/‡πÄ‡∏î‡∏∑‡∏≠‡∏ô</p>
                <div className="mt-2 flex gap-2">
                  <button
                    className="bg-indigo-600 text-white px-3 py-1 rounded"
                    onClick={() => navigate(`/job-detail/${job.job_posting_id}`)}
                  >
                    ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î
                  </button>
                  <button
                    className="bg-yellow-400 text-black px-3 py-1 rounded"
                    onClick={() => navigate(`/job-edit/${job.job_posting_id}`)}
                  >
                    ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç
                  </button>

                  <button
                    className="bg-red-500 text-white px-3 py-1 rounded"
                    onClick={() => handleDelete(job.job_posting_id)}
                  >
                    ‡∏•‡∏ö
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {/* ‚úÖ ‡∏ü‡∏≠‡∏£‡πå‡∏°‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç */}
        {editingJob && (
          <div className="fixed top-0 left-0 w-full h-full bg-black bg-opacity-50 flex justify-center items-center">
            <form
              onSubmit={handleEditSubmit}
              className="bg-white text-black p-6 rounded-lg shadow-md w-full max-w-xl"
            >
              <h2 className="text-xl font-bold mb-4">‚úèÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô</h2>

              {[{ name: 'position', label: '‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á' },
                { name: 'job_description', label: '‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏á‡∏≤‡∏ô' },
                { name: 'requirements', label: '‡∏Ñ‡∏∏‡∏ì‡∏™‡∏°‡∏ö‡∏±‡∏ï‡∏¥' },
                { name: 'compensation', label: '‡∏Ñ‡πà‡∏≤‡∏ï‡∏≠‡∏ö‡πÅ‡∏ó‡∏ô', type: 'number' },
                { name: 'start_date', label: '‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡πÄ‡∏£‡∏¥‡πà‡∏°', type: 'date' },
                { name: 'end_date', label: '‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î', type: 'date' }
              ].map(({ name, label, type = 'text' }) => (
                <div className="mb-4" key={name}>
                  <label className="block font-semibold mb-1">{label}</label>
                  <input
                    type={type}
                    name={name}
                    value={type === 'date' ? (formData[name]?.slice(0, 10) || '') : (formData[name] || '')}
                    onChange={handleChange}
                    className="w-full border rounded px-3 py-2"
                    required
                  />
                </div>
              ))}

              <div className="flex justify-end gap-2">
                <button
                  type="button"
                  onClick={() => setEditingJob(null)}
                  className="bg-gray-400 text-white px-4 py-2 rounded"
                >
                  ‡∏¢‡∏Å‡πÄ‡∏•‡∏¥‡∏Å
                </button>
                <button
                  type="submit"
                  className="bg-green-600 text-white px-4 py-2 rounded"
                >
                  ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å
                </button>
              </div>
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

