// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      switch (role) {
        case 'student':
          localStorage.setItem('studentId', id);
          navigate('/dashboard/student');
          break;
        case 'company':
          localStorage.setItem('companyId', id);
          navigate('/dashboard/company');
          break;
        case 'instructor':
          localStorage.setItem('instructorId', id);
          navigate('/dashboard/instructor');
          break;
        case 'supervisor':
          localStorage.setItem('supervisorId', id);
          navigate('/dashboard/supervisor');
          break;
        case 'admin':
          localStorage.setItem('adminId', id);
          navigate('/admin/approvals');
          break;
        default:
          setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');
          return;
      }

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-400 to-indigo-600">


      <form onSubmit={handleSubmit} className="bg-white w-80 p-6 rounded-lg shadow-md text-center">
        <h2 className="text-2xl font-bold mb-6 text-gray-800">LOGIN</h2>
        <input
          type="email"
          name="email"
          placeholder="Username"
          value={form.email}
          onChange={handleChange}
          required
          className="w-full px-4 py-2 mb-4 border rounded-md bg-gray-100 focus:outline-none"
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
          className="w-full px-4 py-2 mb-4 border rounded-md bg-gray-100 focus:outline-none"
        />
        <div className="flex justify-between items-center mb-4">
          <div></div>
          <a href="/forgot-password" className="text-sm text-indigo-500 hover:underline">?</a>
        </div>
        <button
          type="submit"
          className="w-full bg-indigo-400 hover:bg-indigo-500 text-white font-semibold py-2 rounded-md"
        >
          LOGIN
        </button>
        <p className="text-sm mt-4 text-center">
          ยังไม่มีบัญชี? <a href="/register" className="text-indigo-500 hover:underline">สมัครสมาชิก</a>
        </p>

        {message && <p className="mt-4 text-red-600 text-sm">{message}</p>}
      </form>
    </div>
  );
};

export default LoginPage;
