import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const SupervisorDetailPage = () => {
  const { id } = useParams();
  const [supervisor, setSupervisor] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/supervisor/${id}`)
      .then(res => setSupervisor(res.data))
      .catch(err => {
        console.error("❌ ไม่สามารถโหลดรายละเอียด:", err);
      });
  }, [id]);

  if (!supervisor) {
    return <div className="p-6 text-[#130347]">กำลังโหลดข้อมูล...</div>;
  }

  return (
    <div className="min-h-screen bg-[#9AE5F2] font-sans">
      <Header />

      <div className="mt-8 mx-auto" style={{ maxWidth: '80%' }}>
        {/* หัวข้ออยู่นอกกล่อง */}
        <h2 className="text-2xl font-extrabold mb-4 text-[#130347]">
          รายละเอียดอาจารย์นิเทศ
        </h2>

        {/* กล่องข้อมูล */}
        <div className="bg-white text-[#130347] rounded-2xl shadow-lg border border-[#E6F0FF] p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            
            {/* ข้อมูลอาจารย์ */}
            <div className="space-y-3">
              <p><strong>ชื่อ:</strong> {supervisor.supervisor_name}</p>
              <p><strong>อีเมล:</strong> {supervisor.email}</p>
              <p><strong>เบอร์โทร:</strong> {supervisor.phone_number}</p>
              <p><strong>ภาควิชา:</strong> {supervisor.department}</p>
              <p><strong>คณะ:</strong> {supervisor.faculty}</p>
              <p><strong>ตำแหน่ง:</strong> {supervisor.position}</p>
            </div>

            {/* รูปโปรไฟล์ */}
            {supervisor.profile_image && (
              <div className="flex justify-center items-start">
                <img
                  src={`http://localhost:5000/uploads/${supervisor.profile_image}`}
                  alt="รูปอาจารย์"
                  className="w-40 h-40 object-cover rounded-xl shadow border border-[#E6F0FF]"
                />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SupervisorDetailPage;
