import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import EvaluationCard from '../../components/EvaluationCard';
import { useNavigate } from 'react-router-dom';

const DashboardInstructorScores = () => {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/evaluation/all');
        const rows = Array.isArray(res.data) ? res.data : [];
        // กันค่าประหลาดที่เป็น null/undefined หรือไม่ใช่วัตถุ
        setStudents(rows.filter(r => r && typeof r === 'object'));
      } catch (err) {
        console.error('❌ โหลดข้อมูลล้มเหลว:', err);
        setStudents([]);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-500 to-indigo-700 text-white">
      <Header />
      <div className="p-6 space-y-4">
        {students.length === 0 ? (
          <p className="text-center text-white">ไม่มีข้อมูลการประเมิน</p>
        ) : (
          students.map((stu, idx) => (
            <EvaluationCard
              key={stu?.evaluation_id ?? stu?.student_id ?? idx}
              student={stu}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default DashboardInstructorScores;
