// src/pages/dashboard/DashboardSupervisorEvaluation.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

/* Helpers */
const buildImageUrl = (val) => {
  if (!val) return null;
  if (/^https?:\/\//i.test(val)) return val;
  const file = String(val).split(/[/\\]/).pop();
  return `http://localhost:5000/uploads/${encodeURIComponent(file)}`;
};

/* UI bits */
const PlaceholderAvatar = ({ name }) => {
  const initials = (name || '?')
    .split(' ')
    .map((w) => w[0]?.toUpperCase())
    .slice(0, 2)
    .join('');
  return (
    <div
      className="w-16 h-16 md:w-20 md:h-20 rounded-2xl flex items-center justify-center text-white font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
      aria-label="avatar"
    >
      {initials || 'S'}
    </div>
  );
};

const StatusChip = ({ status }) => {
  const map = {
    completed: 'bg-emerald-50 border-emerald-200 text-emerald-700',
    pending: 'bg-gray-100 border-gray-200 text-gray-700',
  };
  const text = status === 'completed' ? '‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡πÄ‡∏™‡∏£‡πá‡∏à‡∏™‡∏¥‡πâ‡∏ô' : '‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô';
  return (
    <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold border ${map[status] || map.pending}`}>
      {text}
    </span>
  );
};

const Skeleton = () => (
  <div className="rounded-2xl border border-[#E6F0FF] bg-white p-5 shadow-sm animate-pulse">
    <div className="flex items-start gap-4">
      <div className="w-20 h-20 rounded-2xl bg-gray-200" />
      <div className="flex-1 space-y-2">
        <div className="h-4 w-48 bg-gray-200 rounded" />
        <div className="h-3 w-64 bg-gray-200 rounded" />
        <div className="h-3 w-40 bg-gray-200 rounded" />
        <div className="h-3 w-52 bg-gray-200 rounded" />
      </div>
    </div>
    <div className="mt-4 h-9 bg-gray-200 rounded-full w-40" />
  </div>
);

const DashboardSupervisorEvaluation = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const supervisor_id = localStorage.getItem('supervisorId');
        const res = await axios.get(`http://localhost:5000/api/evaluation/students/${supervisor_id}`);
        setStudents(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.error('‚ùå ‡∏î‡∏∂‡∏á‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        setStudents([]);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const handleEvaluate = (student_id) => {
    navigate(`/evaluation/${student_id}`); // ‡πÉ‡∏ä‡πâ‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏î‡∏¥‡∏°‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö ‚Äú‡∏™‡∏£‡πâ‡∏≤‡∏á/‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‚Äù
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-4">
          <h1 className="text-2xl font-extrabold text-[#130347]">üìù ‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡∏ô‡∏¥‡∏™‡∏¥‡∏ï</h1>
          <p className="text-sm text-[#465d71]">‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏Ñ‡∏ß‡∏≤‡∏°‡∏î‡∏π‡πÅ‡∏• ‡∏û‡∏£‡πâ‡∏≠‡∏°‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏Å‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô</p>
        </div>

        {/* Grid */}
        {loading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 2xl:grid-cols-4 gap-4">
            {[...Array(6)].map((_, i) => <Skeleton key={i} />)}
          </div>
        ) : students.length === 0 ? (
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-10 text-center text-[#465d71]">
            ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 2xl:grid-cols-4 gap-4">
            {students.map((s) => {
              const src = buildImageUrl(s.profile_image);
              const evaluated = s.evaluation_status === 'completed';
              return (
                <div
                  key={s.student_id}
                  className="bg-white rounded-2xl border border-[#E6F0FF] p-5 shadow-sm hover:shadow-md transition"
                >
                  <div className="flex items-start gap-4">
                    {src ? (
                      <img
                        src={src}
                        alt="profile"
                        className="w-16 h-16 md:w-20 md:h-20 rounded-2xl object-cover border border-[#E6F0FF]"
                        onError={(e) => (e.currentTarget.style.display = 'none')}
                      />
                    ) : (
                      <PlaceholderAvatar name={s.student_name} />
                    )}

                    <div className="flex-1">
                      <div className="flex items-start justify-between gap-3">
                        <div>
                          <p className="text-lg font-bold text-[#130347]">{s.student_name || '-'}</p>
                          <p className="text-xs text-[#465d71]">‡∏£‡∏´‡∏±‡∏™‡∏ô‡∏¥‡∏™‡∏¥‡∏ï: {s.student_id || '-'}</p>
                        </div>
                        <StatusChip status={s.evaluation_status} />
                      </div>

                      <div className="mt-2 text-sm text-[#130347] space-y-0.5">
                        <p>üìû {s.phone_number || '-'}</p>
                        <p>üìß {s.email || '-'}</p>
                        <p>üè´ {s.university || '-'}</p>
                        <p>üéÇ ‡∏≠‡∏≤‡∏¢‡∏∏ {s.age ?? '-'} ‡∏õ‡∏µ</p>
                      </div>
                    </div>
                  </div>

                  <div className="mt-4 flex justify-end">
                    <button
                      onClick={() => handleEvaluate(s.student_id)}
                      className={`rounded-full px-4 py-2 text-sm font-semibold shadow-sm ${
                        evaluated
                          ? 'bg-[#225EC4] hover:bg-[#1b55b5] text-white'
                          : 'bg-emerald-600 hover:bg-emerald-700 text-white'
                      }`}
                    >
                      {evaluated ? '‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô' : '‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô'}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardSupervisorEvaluation;
