import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

const DashboardStudent = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [province, setProvince] = useState('');
  const [jobType, setJobType] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/job_posting');
      setJobs(res.data);
      setFilteredJobs(res.data);
    } catch (err) {
      console.error("‚ùå ‡πÇ‡∏´‡∏•‡∏î job_posting ‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
    }
  };

  const handleSearch = () => {
    const filtered = jobs.filter((job) => {
      const companyMatch = job.company_name?.toLowerCase().includes(searchTerm.toLowerCase());
      const provinceMatch = job.address?.toLowerCase().includes(province.toLowerCase());
      const typeMatch = job.business_type?.toLowerCase().includes(jobType.toLowerCase());
      return companyMatch && provinceMatch && typeMatch;
    });
    setFilteredJobs(filtered);
  };

  const handleViewMore = (id) => {
    navigate(`/job-detail/${id}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-400 to-indigo-600 font-sans">
      <Header />
      <div className="p-6">
        /*<h1 className="text-3xl font-bold mb-6 text-white">üìö ‡∏´‡∏ô‡πâ‡∏≤‡∏´‡∏•‡∏±‡∏Å‡∏ô‡∏¥‡∏™‡∏¥‡∏ï</h1>*/

        {/* üîç ‡∏ä‡πà‡∏≠‡∏á‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ */}
        <div className="flex flex-wrap gap-2 mb-8">
          <input
            type="text"
            placeholder="‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏ñ‡∏≤‡∏ô‡∏õ‡∏£‡∏∞‡∏Å‡∏≠‡∏ö‡∏Å‡∏≤‡∏£"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="px-4 py-2 rounded w-64 border border-gray-300"
          />
          <input
            type="text"
            placeholder="‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î"
            value={province}
            onChange={(e) => setProvince(e.target.value)}
            className="px-4 py-2 rounded w-40 border border-gray-300"
          />
          <input
            type="text"
            placeholder="‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏á‡∏≤‡∏ô"
            value={jobType}
            onChange={(e) => setJobType(e.target.value)}
            className="px-4 py-2 rounded w-40 border border-gray-300"
          />
          <button
            onClick={handleSearch}
            className="bg-yellow-400 hover:bg-yellow-500 text-white px-4 py-2 rounded"
          >
            üîç ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
          </button>
        </div>

        {/* üîÅ ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡∏á‡∏≤‡∏ô */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-6">
          {filteredJobs.map((job) => (
            <div key={job.job_posting_id} className="bg-white rounded-lg shadow-md p-4 flex gap-4 items-start">
              <div className="flex-shrink-0">
                <img
                  src={`http://localhost:5000/uploads/${job.company_logo || 'default.png'}`}
                  alt="logo"
                  className="w-20 h-20 object-cover rounded-full border"
                />
              </div>
              <div className="flex-1 space-y-1">
                <h3 className="text-xl font-bold text-indigo-700">{job.company_name}</h3>
                <p><span className="font-semibold">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà:</span> {job.address}</p>
                <p><span className="font-semibold">‡∏ï‡∏≥‡πÅ‡∏´‡∏ô‡πà‡∏á:</span> {job.position}</p>
                <p><span className="font-semibold">‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó:</span> {job.business_type}</p>
                <button
                  onClick={() => handleViewMore(job.job_posting_id)}
                  className="mt-2 px-3 py-1 bg-blue-600 hover:bg-blue-700 text-white rounded"
                >
                  üîç ‡∏î‡∏π‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏ï‡∏¥‡∏°
                </button>
              </div>
            </div>
          ))}
        </div>

        {filteredJobs.length === 0 && (
          <p className="text-white mt-6">‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏á‡∏≤‡∏ô‡∏ó‡∏µ‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡πÄ‡∏á‡∏∑‡πà‡∏≠‡∏ô‡πÑ‡∏Ç</p>
        )}
      </div>
    </div>
  );
};

export default DashboardStudent;
