import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const EvaluationCard = ({ student, onUpdateResult }) => {
  const navigate = useNavigate();
  const [currentResult, setCurrentResult] = useState(student.evaluation_result);

  const handleDetailClick = () => {
    navigate(`/student-detail/${student.student_id}`);
  };

  const handleResultChange = async (e) => {
    const selectedValue = e.target.value;
    const mapped = selectedValue === 'ผ่าน' ? 1 : selectedValue === 'ไม่ผ่าน' ? 2 : 0;
    const selectedText = e.target.options[e.target.selectedIndex].text;

    try {
      await axios.put(`http://localhost:5000/api/evaluation/${student.evaluation_id}`, {
        evaluation_result: mapped,
      });

      setCurrentResult(mapped);
      if (onUpdateResult) onUpdateResult(student.evaluation_id, mapped);

      alert(`✅ บันทึกผลการประเมิน "${selectedText}" เรียบร้อยแล้ว`);
    } catch (err) {
      console.error('❌ อัปเดตผลไม่สำเร็จ:', err);
      alert('เกิดข้อผิดพลาดในการอัปเดตผลการประเมิน');
    }
  };

  const reverseMap = {
    1: 'ผ่าน',
    2: 'ไม่ผ่าน',
    0: '',
    null: ''
  };

  const getColorClass = () => {
    if (currentResult === 1) return 'bg-green-500 text-white';
    if (currentResult === 2) return 'bg-red-500 text-white';
    return 'bg-gray-200 text-black';
  };

  return (
    <div className="bg-white rounded-xl shadow-lg border border-blue-200 p-5 mb-5 flex flex-col md:flex-row md:items-center gap-6 text-black">
      <div className="flex gap-5 items-start">
        <img
          src={`http://localhost:5000/uploads/${student.profile_image || 'default.jpg'}`}
          alt="profile"
          className="w-24 h-24 rounded-full object-cover border-4 border-indigo-300"
        />
        <div>
          <h3 className="font-semibold text-xl">{student.student_name}</h3>
          <button
            onClick={handleDetailClick}
            className="text-blue-600 text-sm underline hover:text-blue-800"
          >
            ดูข้อมูลเพิ่มเติม
          </button>

          <div className="mt-3">
            <p>อาจารย์นิเทศ : {student.supervisor_name || '-'}</p>
            <p>คะแนนที่ได้ : {student.supervisor_score ?? '-'} / 100 คะแนน</p>
            <p className="mt-2">สถานประกอบการ : {student.company_name || '-'}</p>
            <p>คะแนนที่ได้ : {student.company_score ?? '-'} / 120 คะแนน</p>
          </div>
        </div>
      </div>

      <div className="ml-auto flex items-center gap-3">
        <span className={`px-4 py-2 rounded-full text-sm font-medium ${getColorClass()}`}>
          {reverseMap[currentResult] || 'ยังไม่ประเมิน'}
        </span>
        <select
          className="border border-gray-300 rounded px-3 py-2 bg-white text-black"
          value={reverseMap[currentResult] || ''}
          onChange={handleResultChange}
        >
          <option value="" disabled>ผลการประเมิน</option>
          <option value="ผ่าน">ผ่าน</option>
          <option value="ไม่ผ่าน">ไม่ผ่าน</option>
        </select>
      </div>
    </div>
  );
};

export default EvaluationCard;
