import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Header from '../../components/Header';

const DashboardCompanyStatus = () => {
  const [applications, setApplications] = useState([]);
  const navigate = useNavigate();
  const company_id = localStorage.getItem('companyId');

  useEffect(() => {
    if (!company_id) return;
    axios
      .get(`http://localhost:5000/api/job_posting/applications/${company_id}`)
      .then((res) => setApplications(res.data))
      .catch((err) => console.error('❌ ดึงข้อมูลนิสิตล้มเหลว:', err));
  }, [company_id]);

  const handleStatusChange = async (application_id, newStatus) => {
    try {
      await axios.put(
        `http://localhost:5000/api/job_posting/application/status/${application_id}`,
        { status: newStatus }
      );

      // อัปเดตสถานะใน state
      setApplications((apps) =>
        apps.map((app) =>
          app.application_id === application_id
            ? { ...app, status: newStatus }
            : app
        )
      );
    } catch (err) {
      console.error('❌ อัปเดตสถานะไม่สำเร็จ:', err);
    }
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2] text-[#130347] font-sans">
      <Header />
      <div className="p-6">
        {applications.length === 0 ? (
          <p>⛔️ ยังไม่มีนิสิตสมัคร</p>
        ) : (
          <div className="space-y-4">
            {applications.map((app) => (
              <div
                key={app.application_id}
                className="bg-white text-black p-4 rounded shadow flex flex-col md:flex-row justify-between items-start md:items-center"
              >
                {/* คอลัมน์ซ้าย: ข้อมูลนิสิต */}
                <div className="space-y-1">
                  <p><strong>รหัสนิสิต:</strong> {app.student_id}</p>
                  <p><strong>ชื่อ:</strong> {app.student_name}</p>
                  <p><strong>อีเมล:</strong> {app.email}</p>
                  <p><strong>เบอร์:</strong> {app.phone_number}</p>
                  <p><strong>คณะ:</strong> {app.faculty}</p>
                  <p><strong>สาขา:</strong> {app.major}</p>
                  <p><strong>วันที่สมัคร:</strong> {new Date(app.apply_date).toLocaleDateString()}</p>
                </div>

                {/* คอลัมน์ขวา: สถานะ + ปุ่ม */}
                <div className="flex flex-col items-end mt-4 md:mt-0 gap-3">
                  <div className="flex items-center gap-2">
                    <label htmlFor="status" className="font-semibold">แก้ไขสถานะ:</label>
                    <select
                      value={app.status}
                      onChange={(e) =>
                        handleStatusChange(
                          app.application_id,
                          parseInt(e.target.value)
                        )
                      }
                      className="border px-3 py-1 rounded"
                    >
                      <option value="1">รับ</option>
                      <option value="2">ไม่รับ</option>
                      <option value="0">อยู่ระหว่างดำเนินการ</option>
                    </select>
                  </div>

                  <div className="flex gap-3">
                    <a
                      href={`http://localhost:5000/uploads/resumes/${app.resume_file}`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                    >
                      📎 ดู Resume
                    </a>
                    <button
                      onClick={() =>
                        navigate(`/student-detail/${app.student_id}`)
                      }
                      className="bg-indigo-600 text-white px-3 py-1 rounded hover:bg-indigo-700"
                    >
                      👁️‍🗨️ ดูเพิ่มเติม
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardCompanyStatus;
