// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      const go = (path, key) => { localStorage.setItem(key, id); navigate(path); };
      if (role === 'student') go('/dashboard/student', 'studentId');
      else if (role === 'company') go('/dashboard/company', 'companyId');
      else if (role === 'instructor') go('/dashboard/instructor', 'instructorId');
      else if (role === 'supervisor') go('/dashboard/supervisor', 'supervisorId');
      else if (role === 'admin') go('/admin/approvals', 'adminId');
      else return setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2 bg-white">
      {/* ซ้าย: โลโก้ใหญ่บนพื้นหลังไล่สี */}
      <div className="relative hidden md:flex items-center justify-center bg-gradient-to-br from-blue-700 via-sky-600 to-blue-500">
        <img
          src="/uploads/InternConnectLogo.png"
          alt="InternConnect"
          className="w-[220px] h-[220px] object-contain drop-shadow-xl"
        />
      </div>

      {/* ขวา: ฟอร์ม Login */}
      <div className="flex items-center justify-center p-8">
        <div className="w-full max-w-md">
          {/* โลโก้เล็กสำหรับจอมือถือ */}
          <div className="md:hidden text-center mb-5">
            <img
              src="/uploads/InternConnectLogo.png"
              alt="InternConnect"
              className="mx-auto w-16 h-16 object-contain"
            />
          </div>

          <h2 className="text-2xl font-bold text-gray-800 mb-2">เข้าสู่ระบบ</h2>
          <p className="text-sm text-gray-500 mb-6">กรอกอีเมลและรหัสผ่านเพื่อเข้าสู่ระบบ</p>

          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="อีเมล"
              value={form.email}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <input
              type="password"
              name="password"
              placeholder="รหัสผ่าน"
              value={form.password}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              type="submit"
              className="w-full py-3 rounded-lg font-semibold bg-blue-600 text-white hover:bg-blue-700 transition"
            >
              ดำเนินการต่อ
            </button>
          </form>

          <p className="text-sm text-center mt-6 text-gray-600">
            ยังไม่มีบัญชี?{' '}
            <Link to="/register" className="text-blue-600 hover:underline">
              สมัครสมาชิก
            </Link>
          </p>

          {message && <p className="text-xs text-red-600 text-center mt-4">{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
