import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

const DashboardSupervisorEvaluation = () => {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const supervisor_id = localStorage.getItem('supervisorId');
        const res = await axios.get(`http://localhost:5000/api/evaluation/students/${supervisor_id}`);
        setStudents(res.data);
      } catch (err) {
        console.error("❌ ดึงรายชื่อนิสิตล้มเหลว:", err);
      }
    };

    fetchStudents();
  }, []);

  const handleEvaluate = (student_id) => {
    navigate(`/evaluation/${student_id}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />

      <div className="p-6">
        

        {students.map((student) => (
          <div
            key={student.student_id}
            className="bg-white text-black p-4 mb-4 rounded-lg shadow"
          >
            <div className="flex items-center space-x-4">
              <img
                src={`http://localhost:5000/uploads/${student.profile_image}`}
                alt={student.student_name}
                className="round-profile"
              />
              <div className="flex-1">
                <p className="text-lg font-semibold">{student.student_name}</p>
                <p>รหัสนิสิต : {student.student_id}</p>
                <p>อายุ : {student.age} ปี</p>
                <p>เบอร์โทร : {student.phone_number}</p>
                <p>มหาวิทยาลัย : {student.university}</p>
              </div>

              {student.evaluation_status === 'completed' ? (
                <span className="text-green-600 font-medium">ประเมินเสร็จสิ้น</span>
              ) : (
                <button
                  className="bg-white text-red-600 font-semibold py-1 px-4 rounded shadow hover:bg-gray-100"
                  onClick={() => handleEvaluate(student.student_id)}
                >
                  ประเมิน
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardSupervisorEvaluation;
