// src/pages/dashboard/DashboardSupervisorEvaluation.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

/* ---------- Small UI helpers ---------- */
const PlaceholderAvatar = ({ name }) => {
  const initials = (name || '?')
    .split(' ')
    .map((w) => w[0]?.toUpperCase())
    .slice(0, 2)
    .join('');
  return (
    <div
      className="w-16 h-16 md:w-20 md:h-20 rounded-2xl flex items-center justify-center text-white font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
      aria-label="avatar"
    >
      {initials || 'S'}
    </div>
  );
};

const Pill = ({ children, className = '' }) => (
  <span className={`inline-flex items-center px-2.5 py-1 rounded-full border text-xs font-semibold ${className}`}>
    {children}
  </span>
);

const EvalChip = ({ status }) => {
  const ok = (status || '').toLowerCase() === 'completed';
  return (
    <span
      className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold border ${
        ok ? 'bg-emerald-50 border-emerald-200 text-emerald-700' : 'bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]'
      }`}
      title={ok ? 'ประเมินเสร็จสิ้น' : 'ยังไม่ประเมิน'}
    >
      {ok ? 'ประเมินเสร็จสิ้น' : 'ยังไม่ประเมิน'}
    </span>
  );
};

/* ---------- Page ---------- */
const DashboardSupervisorEvaluation = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ปรับชื่อฟิลด์ให้เป็นมาตรฐาน (กันเคสแบ็กเอนด์ส่ง key ไม่ตรง)
  const normalize = (x = {}) => {
    const id = x.student_id ?? x.sid ?? x.id ?? '';
    const status =
      x.evaluation_status ??
      (x.score_submitted ? 'completed' : 'pending');
    // แปลง GPA ให้ดูดี
    const gpaNum = Number(x.gpa);
    const gpa =
      Number.isFinite(gpaNum) ? gpaNum.toFixed(2) : (x.gpa ?? '-');

    return {
      id,
      name: x.student_name ?? x.name ?? '-',
      profile_image: x.profile_image ?? '',
      student_id: x.student_id ?? x.sid ?? '-',
      age: x.age ?? null,
      gender: x.gender ?? '',
      gpa,
      phone_number: x.phone_number ?? x.phone ?? '-',
      email: x.email ?? '-',
      university: x.university ?? '-',
      company_name: x.company_name ?? x.company ?? '',
      province: x.province ?? '',
      evaluation_status: status,
    };
  };

  useEffect(() => {
    (async () => {
      try {
        const supervisor_id = localStorage.getItem('supervisorId');
        const { data } = await axios.get(
          `http://localhost:5000/api/evaluation/students/${supervisor_id}`
        );
        const arr = Array.isArray(data) ? data : [];
        setStudents(arr.map(normalize));
      } catch (err) {
        console.error('❌ ดึงรายชื่อนิสิตล้มเหลว:', err);
        setStudents([]);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  const handleEvaluate = (studentId) => {
    navigate(`/evaluation/${studentId}`); // ใช้หน้าฟอร์มเดียวกัน (สร้าง/แก้ไข)
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="w-full max-w-screen-xl mx-auto px-4 lg:px-8 py-8">
        {/* หัวเรื่อง */}
        <div className="mb-4">
          <h1 className="text-2xl font-extrabold text-[#130347]">🧪 ประเมินนิสิต</h1>
          <p className="text-sm text-[#465d71]">
            รายชื่อนิสิตที่คุณดูแล พร้อมสถานะการประเมินและข้อมูลติดต่อ
          </p>
        </div>

        {/* กริดการ์ด: ขนาด/สไตล์ให้เท่ากับหน้า DashboardSupervisor */}
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {[...Array(6)].map((_, i) => (
              <div
                key={i}
                className="rounded-2xl border border-[#E6F0FF] bg-white p-5 shadow-sm animate-pulse"
              >
                <div className="flex items-start gap-4">
                  <div className="w-20 h-20 rounded-2xl bg-gray-200" />
                  <div className="flex-1 space-y-2">
                    <div className="h-4 w-48 bg-gray-200 rounded" />
                    <div className="h-3 w-64 bg-gray-200 rounded" />
                    <div className="h-3 w-40 bg-gray-200 rounded" />
                    <div className="h-3 w-52 bg-gray-200 rounded" />
                  </div>
                </div>
                <div className="mt-4 h-9 bg-gray-200 rounded-full w-40" />
              </div>
            ))}
          </div>
        ) : students.length === 0 ? (
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-10 text-center text-[#465d71]">
            ยังไม่มีนิสิตที่ต้องประเมิน
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 2xl:grid-cols-4 gap-6">
            {students.map((s) => {
              const hasImage = !!s.profile_image;
              const imgSrc = hasImage
                ? `http://localhost:5000/uploads/${s.profile_image}`
                : '';

              return (
                <div
                  key={s.id}
                  className="bg-white rounded-2xl border border-[#E6F0FF] p-5 shadow-sm hover:shadow-md transition"
                >
                  <div className="flex items-start gap-4">
                    {hasImage ? (
                      <img
                        src={imgSrc}
                        alt={s.name}
                        className="w-16 h-16 md:w-20 md:h-20 rounded-2xl object-cover border border-[#E6F0FF]"
                        onError={(e) => {
                          // ถ้ารูปโหลดไม่ได้ ซ่อนรูปแล้วให้ Placeholder ทำงาน
                          e.currentTarget.style.display = 'none';
                        }}
                      />
                    ) : (
                      <PlaceholderAvatar name={s.name} />
                    )}

                    <div className="flex-1">
                      <div className="flex items-start justify-between gap-3">
                        <div>
                          <h2 className="text-lg font-bold text-[#130347]">{s.name}</h2>
                          <p className="text-xs text-[#465d71]">รหัสนิสิต: {s.student_id}</p>
                        </div>
                        <EvalChip status={s.evaluation_status} />
                      </div>

                      {/* แถวชิป: อายุ/เพศ/GPA */}
                      <div className="mt-2 flex flex-wrap gap-2">
                        {s.age ? (
                          <Pill className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                            อายุ {s.age}
                          </Pill>
                        ) : null}
                        {s.gender ? (
                          <Pill className="bg-[#F8FBFF] border-[#E6F0FF] text-[#130347]">
                            เพศ {s.gender}
                          </Pill>
                        ) : null}
                        {s.gpa ? (
                          <Pill className="bg-emerald-50 border-emerald-200 text-emerald-700">
                            GPA {s.gpa}
                          </Pill>
                        ) : null}
                      </div>
                    </div>
                  </div>

                  {/* รายละเอียดสั้น ๆ */}
                  <div className="mt-3 text-sm text-[#130347] space-y-1">
                    <p>📞 {s.phone_number}</p>
                    <p>📧 {s.email}</p>
                    <p>🏫 {s.university}</p>
                    {(s.company_name || s.province) && (
                      <>
                        <p>
                          🏢 <strong>บริษัทที่ฝึก:</strong> {s.company_name || 'ยังไม่ระบุ'}
                        </p>
                        <p>
                          📍 <strong>จังหวัด:</strong> {s.province || 'ยังไม่ระบุ'}
                        </p>
                      </>
                    )}
                  </div>

                  {/* ปุ่มไปหน้าให้คะแนน */}
                  <div className="mt-4">
                    <button
                      onClick={() => handleEvaluate(s.id)}
                      className={`rounded-full text-white text-sm font-semibold px-4 py-2 shadow-sm ${
                        (s.evaluation_status || '').toLowerCase() === 'completed'
                          ? 'bg-indigo-600 hover:bg-indigo-700'
                          : 'bg-emerald-600 hover:bg-emerald-700'
                      }`}
                    >
                      {(s.evaluation_status || '').toLowerCase() === 'completed'
                        ? 'แก้ไขคะแนน'
                        : 'ประเมิน'}
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardSupervisorEvaluation;
