// src/pages/JobDetailPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const JobDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const fromStatusPage = location.state?.fromStatusPage || false;

  const [job, setJob] = useState(null);
  const [userRole, setUserRole] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [resumeFile, setResumeFile] = useState(null);
  const [hasApplied, setHasApplied] = useState(false);

  useEffect(() => {
    if (!id) return;
    const role = localStorage.getItem('role');
    const student_id = localStorage.getItem('studentId');
    setUserRole(role);

    // ดึงรายละเอียดงาน
    axios.get(`http://localhost:5000/api/job_posting/${id}`)
      .then(res => setJob(res.data))
      .catch(err => {
        console.error('❌ ไม่พบข้อมูล:', err);
        alert('ไม่พบข้อมูลประกาศงาน');
        navigate('/dashboard/company');
      });

    // ตรวจสอบการสมัคร (เฉพาะ student)
    if (role === 'student' && student_id) {
      axios.get(`http://localhost:5000/api/job_posting/check-application`, {
        params: { student_id, job_posting_id: id }
      })
        .then(res => setHasApplied(res.data.applied))
        .catch(err => console.error('❌ ตรวจสอบการสมัครล้มเหลว:', err));
    }
  }, [id, navigate]);

  const handleApply = async () => {
    const student_id = localStorage.getItem('studentId');
    if (!student_id || !resumeFile) return alert('กรุณาแนบไฟล์ Resume');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('student_id', student_id);
    formData.append('job_posting_id', id);

    try {
      await axios.post('http://localhost:5000/api/job_posting/apply', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('✅ ส่งใบสมัครสำเร็จแล้ว');
      setShowModal(false);
      setHasApplied(true);
    } catch (err) {
      console.error('❌ สมัครไม่สำเร็จ:', err);
      alert('เกิดข้อผิดพลาดในการสมัคร');
    }
  };

  if (!job) {
    return (
      <div className="min-h-screen bg-[#9AE5F2]">
        <Header />
        <div className="text-center p-10 text-[#130347]">⏳ กำลังโหลด...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      <div className="w-full max-w-3xl mx-auto px-4 lg:px-8 py-8">
        {/* หัวเรื่อง */}
        <div className="mb-4">
          <h2 className="text-2xl font-extrabold text-[#130347]">📋 รายละเอียดประกาศงาน</h2>
          <p className="text-sm text-[#465d71]">ข้อมูลตำแหน่งงานและการติดต่อบริษัท</p>
        </div>

        {/* การ์ดรายละเอียด */}
        <div className="bg-white text-[#130347] rounded-2xl shadow-sm border border-[#E6F0FF] p-6">
          <div className="space-y-2 text-sm leading-6">
            <p><span className="font-semibold text-[#465d71]">ตำแหน่งที่เปิดรับ:</span> {job.position}</p>
            <p><span className="font-semibold text-[#465d71]">ประเภทธุรกิจ:</span> {job.business_type}</p>
            <p><span className="font-semibold text-[#465d71]">รายละเอียดงาน:</span> {job.job_description}</p>
            <p><span className="font-semibold text-[#465d71]">คุณสมบัติที่ต้องการ:</span> {job.requirements}</p>
            <p><span className="font-semibold text-[#465d71]">ค่าตอบแทน:</span> {job.compensation} บาท/เดือน</p>
            <p><span className="font-semibold text-[#465d71]">จำนวนที่รับสมัคร:</span> {job.max_positions}</p>
            <p><span className="font-semibold text-[#465d71]">ที่อยู่บริษัท:</span> {job.address}</p>
            <p>
              <span className="font-semibold text-[#465d71]">Google Maps:</span>{' '}
              <a
                href={job.google_maps_link}
                className="text-[#225EC4] underline hover:text-[#1b55b5]"
                target="_blank"
                rel="noopener noreferrer"
              >
                เปิดแผนที่
              </a>
            </p>
            <p><span className="font-semibold text-[#465d71]">วันที่เริ่มรับสมัคร:</span> {new Date(job.start_date).toLocaleDateString('th-TH')}</p>
            <p><span className="font-semibold text-[#465d71]">วันสิ้นสุดรับสมัคร:</span> {new Date(job.end_date).toLocaleDateString('th-TH')}</p>
            <p><span className="font-semibold text-[#465d71]">อีเมลสำหรับติดต่อ:</span> {job.email}</p>
            <p><span className="font-semibold text-[#465d71]">เบอร์โทรศัพท์:</span> {job.phone_number}</p>
          </div>

          {/* ปุ่มตามบทบาท */}
          {userRole === 'company' && (
            <div className="mt-6 flex gap-2">
              <button
                onClick={() => navigate(`/job-edit/${id}`)}
                className="rounded-full bg-[#225EC4] hover:bg-[#1b55b5] text-white px-5 py-2 font-semibold shadow-sm"
              >
                ✏️ แก้ไข
              </button>
              <button
                onClick={async () => {
                  if (window.confirm('คุณแน่ใจหรือไม่ว่าต้องการลบประกาศนี้?')) {
                    try {
                      await axios.delete(`http://localhost:5000/api/job_posting/${id}`);
                      alert('✅ ลบประกาศเรียบร้อยแล้ว');
                      navigate('/dashboard/company');
                    } catch (err) {
                      console.error('❌ ลบไม่สำเร็จ:', err);
                      alert('เกิดข้อผิดพลาดในการลบ');
                    }
                  }
                }}
                className="rounded-full bg-rose-600 hover:bg-rose-700 text-white px-5 py-2 font-semibold shadow-sm"
              >
                🗑️ ลบ
              </button>
            </div>
          )}

          {userRole === 'student' && !fromStatusPage && !hasApplied && (
            <div className="mt-6">
              <button
                onClick={() => setShowModal(true)}
                className="rounded-full bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2 font-semibold shadow-sm"
              >
                ✅ สมัคร
              </button>
            </div>
          )}

          {userRole === 'student' && hasApplied && (
            <p className="mt-6 text-emerald-600 font-semibold">✅ คุณได้สมัครงานนี้แล้ว</p>
          )}
        </div>
      </div>

      {/* Modal อัปโหลดเรซูเม่ */}
      {showModal && (
        <div className="fixed inset-0 bg-black/40 flex justify-center items-center z-50 p-4">
          <div className="bg-white rounded-2xl p-6 w-full max-w-md border border-[#E6F0FF] shadow-lg">
            <h2 className="text-xl font-bold text-[#130347] mb-3 text-center">ส่ง Resume</h2>
            <input
              type="file"
              accept=".pdf"
              onChange={e => setResumeFile(e.target.files[0])}
              className="mb-4 w-full rounded-xl border border-[#E6F0FF] bg-[#F8FBFF] px-3 py-2 outline-none focus:ring-2 focus:ring-[#6EC7E2] text-[#130347]"
            />
            <div className="flex justify-center gap-3">
              <button
                onClick={() => setShowModal(false)}
                className="rounded-full bg-rose-600 hover:bg-rose-700 text-white px-6 py-2 font-semibold shadow-sm"
              >
                ยกเลิก
              </button>
              <button
                onClick={handleApply}
                className="rounded-full bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2 font-semibold shadow-sm"
              >
                ยืนยัน
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default JobDetailPage;
