// src/pages/dashboard/DashboardSupervisorEvaluation.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';
import { useNavigate } from 'react-router-dom';

/** ‡∏≠‡∏ß‡∏ï‡∏≤‡∏£‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£‡∏¢‡πà‡∏≠ (‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏£‡∏π‡∏õ) */
const PlaceholderAvatar = ({ name }) => {
  const initials = (name || '?')
    .split(' ')
    .map((w) => w[0]?.toUpperCase())
    .slice(0, 2)
    .join('');
  return (
    <div
      className="w-16 h-16 md:w-20 md:h-20 rounded-2xl flex items-center justify-center text-white font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
      aria-label="avatar"
    >
      {initials || 'S'}
    </div>
  );
};

const StatusBadge = ({ status }) => {
  const isDone = status === 'completed';
  return (
    <span
      className={
        'px-2.5 py-1 rounded-full text-xs font-semibold ' +
        (isDone ? 'bg-emerald-100 text-emerald-700' : 'bg-gray-100 text-gray-700')
      }
      title={isDone ? '‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡πÄ‡∏™‡∏£‡πá‡∏à‡∏™‡∏¥‡πâ‡∏ô' : '‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô'}
    >
      {isDone ? '‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡πÄ‡∏™‡∏£‡πá‡∏à‡∏™‡∏¥‡πâ‡∏ô' : '‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô'}
    </span>
  );
};

const DashboardSupervisorEvaluation = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const supervisor_id = localStorage.getItem('supervisorId');
        const res = await axios.get(
          `http://localhost:5000/api/evaluation/students/${supervisor_id}`
        );
        setStudents(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.error('‚ùå ‡∏î‡∏∂‡∏á‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:', err);
        setStudents([]);
      } finally {
        setLoading(false);
      }
    };
    fetchStudents();
  }, []);

  const handleEvaluate = (student_id) => {
    // ‡πÉ‡∏ä‡πâ‡∏´‡∏ô‡πâ‡∏≤‡πÄ‡∏î‡∏µ‡∏¢‡∏ß /evaluation/:id ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏ó‡∏±‡πâ‡∏á ‚Äú‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà‚Äù ‡πÅ‡∏•‡∏∞ ‚Äú‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç‚Äù
    navigate(`/evaluation/${student_id}`);
  };

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      {/* ‡∏Ç‡∏¢‡∏≤‡∏¢‡∏Ñ‡∏ß‡∏≤‡∏°‡∏Å‡∏ß‡πâ‡∏≤‡∏á‡∏™‡∏π‡∏á‡∏™‡∏∏‡∏î‡πÄ‡∏õ‡πá‡∏ô 2XL ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏•‡∏î‡∏û‡∏∑‡πâ‡∏ô‡∏ó‡∏µ‡πà‡∏Ç‡πâ‡∏≤‡∏á ‡πÜ */}
      <div className="w-full max-w-screen-2xl mx-auto px-4 lg:px-8 py-8">
        {/* ‡∏´‡∏±‡∏ß‡πÄ‡∏£‡∏∑‡πà‡∏≠‡∏á */}
        <div className="mb-4">
          <h1 className="text-2xl font-extrabold text-[#130347]">üìù ‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô‡∏ô‡∏¥‡∏™‡∏¥‡∏ï</h1>
          <p className="text-sm text-[#465d71]">
            ‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏Ñ‡∏ß‡∏≤‡∏°‡∏î‡∏π‡πÅ‡∏• ‡∏û‡∏£‡πâ‡∏≠‡∏°‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏Å‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô
          </p>
        </div>

        {/* ‡∏Å‡∏£‡∏¥‡∏î‡∏Å‡∏≤‡∏£‡πå‡∏î‡∏ô‡∏¥‡∏™‡∏¥‡∏ï */}
        {loading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {[...Array(8)].map((_, i) => (
              <div
                key={i}
                className="rounded-2xl border border-[#E6F0FF] bg-white p-5 shadow-sm animate-pulse h-full"
              >
                <div className="flex items-start gap-4">
                  <div className="w-20 h-20 rounded-2xl bg-gray-200" />
                  <div className="flex-1 space-y-2">
                    <div className="h-4 w-48 bg-gray-200 rounded" />
                    <div className="h-3 w-64 bg-gray-200 rounded" />
                    <div className="h-3 w-40 bg-gray-200 rounded" />
                    <div className="h-3 w-52 bg-gray-200 rounded" />
                  </div>
                </div>
                <div className="mt-4 h-9 bg-gray-200 rounded-full w-40" />
              </div>
            ))}
          </div>
        ) : students.length === 0 ? (
          <div className="bg-white border border-[#E6F0FF] rounded-2xl p-10 text-center text-[#465d71]">
            ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏ô‡∏¥‡∏™‡∏¥‡∏ï‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏°‡∏¥‡∏ô
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {students.map((st) => {
              const hasImage = !!st.profile_image;
              const imgSrc = hasImage ? `http://localhost:5000/uploads/${st.profile_image}` : '';
              const status = st.evaluation_status === 'completed' ? 'completed' : 'pending';

              return (
                <div
                  key={st.student_id}
                  className="bg-white rounded-2xl border border-[#E6F0FF] p-5 shadow-sm hover:shadow-md transition h-full flex flex-col"
                >
                  {/* ‡∏™‡πà‡∏ß‡∏ô‡∏´‡∏±‡∏ß‡∏Å‡∏≤‡∏£‡πå‡∏î */}
                  <div className="flex items-start gap-4">
                    {hasImage ? (
                      <img
                        src={imgSrc}
                        alt={st.student_name}
                        className="w-16 h-16 md:w-20 md:h-20 rounded-2xl object-cover border border-[#E6F0FF]"
                        onError={(e) => {
                          e.currentTarget.style.display = 'none';
                        }}
