import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../../components/Header';

const DashboardAdminProfile = () => {
  const [adminId, setAdminId] = useState(null);
  const [admin, setAdmin] = useState({
    admin_id: '',
    admin_name: '',
    email: '',
    phone_number: '',
    role: 'admin',
    profile_image: ''
  });

  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState('');
  const [formError, setFormError] = useState('');

  useEffect(() => {
    const id = localStorage.getItem("adminId");
    if (id) {
      setAdminId(id);
      setAdmin(prev => ({ ...prev, admin_id: id }));
    } else {
      setError("‡πÑ‡∏°‡πà‡∏û‡∏ö adminId ‡∏Å‡∏£‡∏∏‡∏ì‡∏≤ login ‡πÉ‡∏´‡∏°‡πà");
    }
  }, []);

  useEffect(() => {
    if (!adminId) return;

    axios.get(`http://localhost:5000/api/admin/${adminId}`)
      .then(res => {
        if (res.data) {
          setAdmin(prev => ({ ...prev, ...res.data }));
        }
      })
      .catch(err => {
        console.error("‚ùå ‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
        setError("‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÇ‡∏´‡∏•‡∏î‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•");
      });
  }, [adminId]);

  const handleChange = (e) => {
    setAdmin({ ...admin, [e.target.name]: e.target.value });
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  const validateForm = () => {
    const { admin_name, email, phone_number } = admin;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{9,12}$/;

    if (!admin_name.trim()) return "‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠";
    if (!emailRegex.test(email)) return "‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á";
    if (!phoneRegex.test(phone_number)) return "‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á (‡∏ï‡πâ‡∏≠‡∏á‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç 9-12 ‡∏´‡∏•‡∏±‡∏Å)";
    return null;
  };

  const handleSave = async () => {
    const validationError = validateForm();
    if (validationError) {
      setFormError(validationError);
      return;
    }

    try {
      let profileImageFilename = admin.profile_image;

      if (selectedFile) {
        const formData = new FormData();
        formData.append('image', selectedFile);
        const res = await axios.post('http://localhost:5000/api/upload/profile-image', formData);
        profileImageFilename = res.data.filename;
      }

      const updatedAdmin = { ...admin, profile_image: profileImageFilename };

      await axios.put(`http://localhost:5000/api/admin/${adminId}`, updatedAdmin);
      localStorage.setItem('profile_image', profileImageFilename);
      alert("‚úÖ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß");
      setFormError('');
    } catch (err) {
      console.error("‚ùå ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß:", err);
      setError("‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-700 text-white font-sans">
      <Header />
      <div className="p-6 mt-10 max-w-xl mx-auto bg-white text-black rounded-lg shadow">
        <h1 className="text-3xl font-bold mb-4 text-indigo-700">üë§ ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•</h1>

        {error && <p className="text-red-500">{error}</p>}
        {formError && <p className="text-red-500">{formError}</p>}

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-indigo-700">‡∏£‡∏´‡∏±‡∏™‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•</label>
            <input name="admin_id" value={admin.admin_id} disabled className="w-full border rounded p-2 bg-gray-100" />
          </div>

          <div>
            <label className="block text-sm font-medium text-indigo-700">‡∏ä‡∏∑‡πà‡∏≠</label>
            <input name="admin_name" value={admin.admin_name} onChange={handleChange} className="w-full border rounded p-2" />
          </div>

          <div>
            <label className="block text-sm font-medium text-indigo-700">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</label>
            <input name="email" value={admin.email} onChange={handleChange} className="w-full border rounded p-2" />
          </div>

          <div>
            <label className="block text-sm font-medium text-indigo-700">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£</label>
            <input name="phone_number" value={admin.phone_number} onChange={handleChange} className="w-full border rounded p-2" />
          </div>

          <div>
            <label className="block text-sm font-medium text-indigo-700">‡∏ö‡∏ó‡∏ö‡∏≤‡∏ó</label>
            <input name="role" value={admin.role} disabled className="w-full border rounded p-2 bg-gray-100" />
          </div>

          <div>
            <label className="block text-sm font-medium text-indigo-700">‡∏≠‡∏±‡∏õ‡πÇ‡∏´‡∏•‡∏î‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå</label>
            <input type="file" accept="image/*" onChange={handleImageUpload} className="w-full" />
          </div>

          {admin.profile_image && (
            <div className="flex justify-center mb-4">
              <div className="w-32 h-32 rounded-full overflow-hidden shadow">
                <img
                  src={`http://localhost:5000/uploads/${admin.profile_image}`}
                  alt="‡∏£‡∏π‡∏õ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå"
                  className="object-cover w-full h-full"
                />
              </div>
            </div>
          )}

          <button
            onClick={handleSave}
            className="w-full bg-indigo-600 text-white py-2 mt-4 rounded hover:bg-indigo-700"
          >
            üíæ ‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å
          </button>
        </div>
      </div>
    </div>
  );
};

export default DashboardAdminProfile;
