            {[5, 4, 3, 2, 1].map(score => (
              <th key={score} className="text-center">{score}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index} className={index % 2 === 0 ? 'bg-gray-100' : 'bg-white'}>
              <td className="p-2">{index + 1}. {item}</td>
