// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [showPw, setShowPw] = useState(false);
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      const go = (path, key) => { localStorage.setItem(key, id); navigate(path); };
      if (role === 'student') go('/dashboard/student', 'studentId');
      else if (role === 'company') go('/dashboard/company', 'companyId');
      else if (role === 'instructor') go('/dashboard/instructor', 'instructorId');
      else if (role === 'supervisor') go('/dashboard/supervisor', 'supervisorId');
      else if (role === 'admin') go('/admin/approvals', 'adminId');
      else return setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');

      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-1 lg:grid-cols-2">

      {/* LEFT: brand panel (blue gradient) */}
      <div className="relative hidden lg:block overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-br from-sky-500 via-sky-600 to-blue-600" />
        {/* subtle grid */}
        <div
          className="absolute inset-0 opacity-10"
          style={{
            backgroundImage:
              'linear-gradient(transparent 23px, rgba(255,255,255,.15) 24px),linear-gradient(90deg, transparent 23px, rgba(255,255,255,.15) 24px)',
            backgroundSize: '24px 24px'
          }}
        />
        {/* logo top-left */}
        <div className="relative z-10 p-8 flex items-center gap-3">
          <img src="/uploads/InternConnectLogo.png" alt="InternConnect" className="w-9 h-9 object-contain" />
          <span className="text-white/95 font-semibold text-xl">InternConnect</span>
        </div>
        {/* hero mock/illustration */}
        <div className="relative z-10 px-10 pt-2">
          {/* ใส่ภาพประกอบของคุณเองแทน src นี้ได้ (เช่น mockup หน้าระบบ) */}
          {/* ถ้าไม่มี จะใช้กล่องตัวอย่างด้านล่างแทน */}
          <div className="mx-auto max-w-3xl bg-white/90 backdrop-blur rounded-2xl shadow-2xl p-6">
            <div className="h-64 rounded-xl bg-gradient-to-br from-sky-50 to-white border border-white/60 shadow-inner" />
            <div className="mt-4 flex gap-3">
              <div className="flex-1 h-24 rounded-xl bg-white/90 border border-white/70" />
              <div className="w-40 h-24 rounded-xl bg-white/90 border border-white/70" />
            </div>
          </div>
        </div>
      </div>

      {/* RIGHT: form */}
      <div className="flex items-center justify-center p-6 lg:p-10 bg-white">
        <div className="w-full max-w-md">
          {/* mobile logo */}
          <div className="lg:hidden text-center mb-6">
            <img src="/uploads/InternConnectLogo.png" alt="InternConnect" className="mx-auto w-12 h-12" />
          </div>

          <h1 className="text-2xl font-bold text-slate-800">เข้าสู่ระบบ</h1>

          {/* Social buttons */}
          <div className="mt-6 space-y-3">
            <button
              type="button"
              className="w-full flex items-center justify-center gap-3 rounded-lg border border-slate-200 py-2.5 hover:bg-slate-50 transition"
            >
              {/* Google icon */}
              <svg viewBox="0 0 48 48" className="w-5 h-5">
                <path fill="#FFC107" d="M43.6 20.5H42V20H24v8h11.3c-1.6 4.6-6 8-11.3 8-6.6 0-12-5.4-12-12s5.4-12 12-12c3 0 5.7 1.1 7.8 3l5.7-5.7C33.4 6.2 28.9 4 24 4 12.9 4 4 12.9 4 24s8.9 20 20 20 20-8.9 20-20c0-1.2-.1-2.3-.4-3.5z"/>
                <path fill="#FF3D00" d="M6.3 14.7l6.6 4.8C14.9 16.1 19.1 14 24 14c3 0 5.7 1.1 7.8 3l5.7-5.7C33.4 6.2 28.9 4 24 4 16 4 9.2 8.6 6.3 14.7z"/>
                <path fill="#4CAF50" d="M24 44c5 0 9.6-1.9 13-5l-6-4.9c-2 1.4-4.6 2.3-7 2.3-5.3 0-9.7-3.4-11.3-8H6.3l-6 4.6C3.1 39.4 12.6 44 24 44z"/>
                <path fill="#1976D2" d="M44 24c0-1.2-.1-2.3-.4-3.5H24v8h11.3c-.8 2.5-2.4 4.6-4.6 6.1l6 4.9C40.8 36.8 44 30.9 44 24z"/>
              </svg>
              <span className="text-slate-700 text-sm">เข้าสู่ระบบด้วย Google</span>
            </button>
            <button
              type="button"
              className="w-full flex items-center justify-center gap-3 rounded-lg border border-slate-200 py-2.5 hover:bg-slate-50 transition"
            >
              {/* LINE icon (simple) */}
              <svg viewBox="0 0 36 36" className="w-5 h-5"><rect rx="8" width="36" height="36" fill="#06C755"/><path d="M24.8 18.3c0 3-3 5.4-6.8 5.4-.7 0-1.5-.1-2.2-.3l-3.3 2 .8-3c-1.3-1-2.2-2.4-2.2-4 0-3 3-5.4 6.8-5.4s6.9 2.4 6.9 5.3Z" fill="#fff"/></svg>
              <span className="text-slate-700 text-sm">เข้าสู่ระบบด้วย LINE</span>
            </button>
          </div>

          {/* divider */}
          <div className="flex items-center gap-3 my-6">
            <div className="h-px bg-slate-200 flex-1" />
            <span className="text-slate-400 text-xs">หรือเข้าสู่ระบบด้วยอีเมล</span>
            <div className="h-px bg-slate-200 flex-1" />
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="อีเมล"
              value={form.email}
              onChange={handleChange}
              required
              className="w-full px-4 py-3 rounded-lg border border-slate-300 bg-white focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400"
            />

            <div className="relative">
              <input
                type={showPw ? 'text' : 'password'}
                name="password"
                placeholder="รหัสผ่าน"
                value={form.password}
                onChange={handleChange}
                required
                className="w-full px-4 py-3 rounded-lg border border-slate-300 bg-white focus:outline-none focus:ring-4 focus:ring-sky-200 focus:border-sky-400"
              />
              <button
                type="button"
                onClick={() => setShowPw(s => !s)}
                className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-500 hover:text-slate-700"
                aria-label="toggle password"
              >
                {/* eye icon */}
                {showPw ? (
                  <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none"><path d="M2 12s3.5-7 10-7 10 7 10 7-3.5 7-10 7S2 12 2 12Z" stroke="currentColor"/><circle cx="12" cy="12" r="3" stroke="currentColor"/></svg>
                ) : (
                  <svg className="w-5 h-5" viewBox="0 0 24 24" fill="none"><path d="M3 3l18 18" stroke="currentColor"/><path d="M2 12s3.5-7 10-7c2.2 0 4.1.7 5.7 1.7M22 12s-3.5 7-10 7c-2.2 0-4.1-.7-5.7-1.7" stroke="currentColor"/></svg>
                )}
              </button>
            </div>

            <div className="text-right">
              <Link to="/forgot-password" className="text-sky-700 text-sm hover:underline">
                ลืมรหัสผ่าน?
              </Link>
            </div>

            <button
              type="submit"
              className="w-full py-3 rounded-full font-semibold bg-gradient-to-r from-sky-500 to-blue-600 text-white hover:opacity-95 active:opacity-90 transition"
            >
              เข้าสู่ระบบ
            </button>

            <p className="text-sm text-center text-slate-600">
              ยังไม่มีบัญชี?{' '}
              <Link to="/register" className="text-sky-700 hover:underline">
                สมัครบัญชีใหม่
              </Link>
            </p>

            {message && <p className="text-xs text-red-600 text-center">{message}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
