// src/pages/LoginPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPw, setShowPw] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      const { token, role, id, email, name } = res.data;

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      localStorage.setItem('id', id);
      localStorage.setItem('email', email);
      localStorage.setItem('name', name);

      switch (role) {
        case 'student':
          localStorage.setItem('studentId', id);
          navigate('/dashboard/student');
          break;
        case 'company':
          localStorage.setItem('companyId', id);
          navigate('/dashboard/company');
          break;
        case 'instructor':
          localStorage.setItem('instructorId', id);
          navigate('/dashboard/instructor');
          break;
        case 'supervisor':
          localStorage.setItem('supervisorId', id);
          navigate('/dashboard/supervisor');
          break;
        case 'admin':
          localStorage.setItem('adminId', id);
          navigate('/admin/approvals');
          break;
        default:
          setMessage('บทบาทผู้ใช้ไม่ถูกต้อง');
          return;
      }
      setMessage('เข้าสู่ระบบสำเร็จ');
    } catch (err) {
      setMessage(err.response?.data?.message || 'เกิดข้อผิดพลาด');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-600 via-blue-700 to-blue-900">
      <div className="absolute inset-0 opacity-20 bg-[radial-gradient(circle_at_20%_20%,white,transparent_25%),radial-gradient(circle_at_80%_30%,white,transparent_20%),radial-gradient(circle_at_50%_80%,white,transparent_20%)]" />

      <form
        onSubmit={handleSubmit}
        className="relative z-10 w-96 max-w-[92%] bg-white/90 backdrop-blur-md p-7 rounded-2xl shadow-xl border border-white/60"
      >
        <div className="flex items-center justify-center gap-2 mb-6">
          <div className="h-10 w-10 rounded-xl bg-blue-600 flex items-center justify-center text-white font-bold">IT</div>
          <h2 className="text-2xl font-bold text-gray-800 tracking-wide">InternConnect • Login</h2>
        </div>

        <label className="block text-sm font-medium text-gray-700 mb-1">อีเมล</label>
        <div className="relative mb-4">
          <input
            type="email"
            name="email"
            placeholder="you@it.example.ac.th"
            value={form.email}
            onChange={handleChange}
            required
            className="w-full px-4 py-2.5 pr-10 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
          <svg className="absolute right-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M21.75 7.5l-9.75 6-9.75-6m19.5 0A2.25 2.25 0 0019.5 5.25h-15A2.25 2.25 0 002.25 7.5v9A2.25 2.25 0 004.5 18.75h15a2.25 2.25 0 002.25-2.25v-9z" /></svg>
        </div>

        <label className="block text-sm font-medium text-gray-700 mb-1">รหัสผ่าน</label>
        <div className="relative mb-2">
          <input
            type={showPw ? 'text' : 'password'}
            name="password"
            placeholder="••••••••"
            value={form.password}
            onChange={handleChange}
            required
            className="w-full px-4 py-2.5 pr-10 rounded-lg border border-gray-300 bg-white focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
          <button
            type="button"
            onClick={() => setShowPw((v) => !v)}
            className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500 hover:text-gray-700"
            aria-label={showPw ? 'ซ่อนรหัสผ่าน' : 'แสดงรหัสผ่าน'}
          >
            {showPw ? (
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M3.98 8.223A10.477 10.477 0 001.5 12c1.5 3 5.25 7.5 10.5 7.5 1.79 0 3.463-.42 4.98-1.177M6.228 6.228A10.45 10.45 0 0112 4.5c5.25 0 9 4.5 10.5 7.5a10.494 10.494 0 01-1.594 2.485M6.228 6.228L3 3m3.228 3.228l11.544 11.544M9.75 9.75a3 3 0 004.5 4.5" /></svg>
            ) : (
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M2.036 12.322C3.423 9.31 7.03 5.25 12 5.25c4.97 0 8.577 4.06 9.964 7.072a1.125 1.125 0 010 .856C20.577 16.19 16.97 20.25 12 20.25c-4.97 0-8.577-4.06-9.964-7.072a1.125 1.125 0 010-.856z" /><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="1.5" d="M15.75 12a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z" /></svg>
            )}
          </button>
        </div>

        <div className="flex items-center justify-between mb-5">
          <div className="flex items-center gap-2">
            <input id="remember" type="checkbox" className="h-4 w-4 rounded border-gray-300 text-blue-600 focus:ring-blue-500" />
            <label htmlFor="remember" className="text-sm text-gray-700">จดจำฉันไว้</label>
          </div>
          <a href="/forgot-password" className="text-sm text-blue-600 hover:underline">ลืมรหัสผ่าน?</a>
        </div>

        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-600 hover:bg-blue-700 disabled:opacity-60 text-white font-semibold py-2.5 rounded-lg shadow focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition"
        >
          {loading ? 'กำลังเข้าสู่ระบบ…' : 'เข้าสู่ระบบ'}
        </button>

        <p className="text-sm mt-5 text-center text-gray-700">
          ยังไม่มีบัญชี?{' '}
          <a href="/register" className="text-blue-600 hover:underline">สมัครสมาชิก</a>
        </p>

        {message && (
          <div className={`mt-4 text-sm rounded-lg px-3 py-2 ${
            message.includes('สำเร็จ')
              ? 'bg-green-50 text-green-700 border border-green-200'
              : 'bg-red-50 text-red-700 border border-red-200'
          }`}>
            {message}
          </div>
        )}
      </form>
    </div>
  );
};

export default LoginPage;
