// src/pages/JobDetailPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import Header from '../components/Header';

const PlaceholderLogo = ({ name }) => {
  const initials = (name || '?')
    .split(' ')
    .map(w => w[0]?.toUpperCase())
    .slice(0, 2)
    .join('');
  return (
    <div
      className="w-16 h-16 rounded-2xl flex items-center justify-center text-white font-bold shadow-sm"
      style={{ backgroundColor: '#4691D3' }}
    >
      {initials || 'C'}
    </div>
  );
};

const JobDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const fromStatusPage = location.state?.fromStatusPage || false;

  const [job, setJob] = useState(null);
  const [userRole, setUserRole] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [resumeFile, setResumeFile] = useState(null);
  const [hasApplied, setHasApplied] = useState(false);

  useEffect(() => {
    if (!id) return;
    const role = localStorage.getItem('role');
    const student_id = localStorage.getItem('studentId');
    setUserRole(role);

    axios.get(`http://localhost:5000/api/job_posting/${id}`)
      .then(res => setJob(res.data))
      .catch(() => {
        alert('‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏á‡∏≤‡∏ô');
        navigate('/dashboard/company');
      });

    if (role === 'student' && student_id) {
      axios.get(`http://localhost:5000/api/job_posting/check-application`, {
        params: { student_id, job_posting_id: id }
      })
      .then(res => setHasApplied(res.data.applied))
      .catch(() => {});
    }
  }, [id, navigate]);

  const handleApply = async () => {
    const student_id = localStorage.getItem('studentId');
    if (!student_id || !resumeFile) return alert('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡πÅ‡∏ô‡∏ö‡πÑ‡∏ü‡∏•‡πå Resume');

    const formData = new FormData();
    formData.append('resume', resumeFile);
    formData.append('student_id', student_id);
    formData.append('job_posting_id', id);

    try {
      await axios.post('http://localhost:5000/api/job_posting/apply', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('‚úÖ ‡∏™‡πà‡∏á‡πÉ‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß');
      setShowModal(false);
      setHasApplied(true);
    } catch {
      alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏™‡∏°‡∏±‡∏Ñ‡∏£');
    }
  };

  if (!job) {
    return (
      <div className="min-h-screen bg-[#9AE5F2]">
        <Header />
        <div className="text-center p-10 text-[#130347]">‚è≥ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î...</div>
      </div>
    );
  }

  const isEmbedMap = typeof job.google_maps_link === 'string' &&
    job.google_maps_link.startsWith('https://www.google.com/maps/embed');

  return (
    <div className="min-h-screen bg-[#9AE5F2]">
      <Header />

      {/* HERO */}
      <div className="w-full max-w-screen-lg mx-auto px-4 lg:px-8 pt-8">
        <div className="rounded-2xl bg-gradient-to-r from-[#6EC7E2] to-[#4691D3] text-white p-5 shadow">
          <div className="flex items-start justify-between gap-4">
            <div className="flex items-start gap-4">
              {job.company_logo ? (
                <img
                  src={`http://localhost:5000/uploads/${job.company_logo}`}
                  alt="logo"
                  className="w-16 h-16 rounded-2xl object-cover border border-white/40"
                />
              ) : (
                <PlaceholderLogo name={job.company_name} />
              )}
              <div>
                <h1 className="text-xl font-extrabold">{job.position}</h1>
                <p className="text-white/90">{job.company_name}</p>
                <div className="flex flex-wrap gap-2 mt-2">
                  <span className="bg-white/15 px-3 py-1 rounded-full text-xs">üè∑Ô∏è {job.business_type}</span>
                  <span className="bg-white/15 px-3 py-1 rounded-full text-xs">üìç {job.address}</span>
                  <span className="bg-white/15 px-3 py-1 rounded-full text-xs">üë§ ‡∏£‡∏±‡∏ö {job.max_positions} ‡∏Ñ‡∏ô</span>
                  <span className="bg-white/15 px-3 py-1 rounded-full text-xs">üí∏ {Number(job.compensation).toLocaleString()} ‡∏ö‡∏≤‡∏ó/‡πÄ‡∏î‡∏∑‡∏≠‡∏ô</span>
                </div>
              </div>
            </div>

            <div className="hidden sm:block">
              <button
                onClick={() => navigate(-1)}
                className="rounded-full bg-white/20 hover:bg-white/30 px-4 py-2 text-sm font-semibold"
              >
                ‚Üê ‡∏Å‡∏•‡∏±‡∏ö
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* MAIN GRID */}
      <div className="w-full max-w-screen-lg mx-auto px-4 lg:px-8 py-6 grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* LEFT */}
        <div className="lg:col-span-2 space-y-6">
          <div className="bg-white rounded-2xl border border-[#E6F0FF] shadow-sm p-5">
            <h3 className="font-bold text-[#130347] mb-2">‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏á‡∏≤‡∏ô</h3>
            <p className="text-[#130347] text-sm leading-7 whitespace-pre-line">
              {job.job_description || '-'}
            </p>
          </div>

          <div className="bg-white rounded-2xl border border-[#E6F0FF] shadow-sm p-5">
            <h3 className="font-bold text-[#130347] mb-2">‡∏Ñ‡∏∏‡∏ì‡∏™‡∏°‡∏ö‡∏±‡∏ï‡∏¥‡∏ó‡∏µ‡πà‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£</h3>
            <p className="text-[#130347] text-sm leading-7 whitespace-pre-line">
              {job.requirements || '-'}
            </p>
          </div>
        </div>

        {/* RIGHT (Sticky) */}
        <aside className="lg:sticky lg:top-6 space-y-6">
          <div className="bg-white rounded-2xl border border-[#E6F0FF] shadow-sm p-5">
            <h4 className="font-bold text-[#130347] mb-3">‡∏™‡∏£‡∏∏‡∏õ‡∏Å‡∏≤‡∏£‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£</h4>
            <ul className="text-sm text-[#130347] space-y-1">
              <li>üìÖ ‡πÄ‡∏£‡∏¥‡πà‡∏°‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£: {new Date(job.start_date).toLocaleDateString('th-TH')}</li>
              <li>‚è≥ ‡∏™‡∏¥‡πâ‡∏ô‡∏™‡∏∏‡∏î‡∏£‡∏±‡∏ö‡∏™‡∏°‡∏±‡∏Ñ‡∏£: {new Date(job.end_date).toLocaleDateString('th-TH')}</li>
              <li>üìß ‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠: {job.email}</li>
              <li>üìû ‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£: {job.phone_number}</li>
            </ul>

            {/* Action by role */}
            <div className="mt-4 flex flex-wrap gap-2">
              {userRole === 'company' && (
                <>
                  <button
                    onClick={() => navigate(`/job-edit/${id}`)}
                    className="rounded-full bg-[#225EC4] hover:bg-[#1b55b5] text-white px-4 py-2 text-sm font-semibold shadow-sm"
                  >
                    ‚úèÔ∏è ‡πÅ‡∏Å‡πâ‡πÑ‡∏Ç
                  </button>
                  <button
                    onClick={async () => {
                      if (window.confirm('‡∏Ñ‡∏∏‡∏ì‡πÅ‡∏ô‡πà‡πÉ‡∏à‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏ß‡πà‡∏≤‡∏ï‡πâ‡∏≠‡∏á‡∏Å‡∏≤‡∏£‡∏•‡∏ö‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡∏ô‡∏µ‡πâ?')) {
                        try {
                          await axios.delete(`http://localhost:5000/api/job_posting/${id}`);
                          alert('‚úÖ ‡∏•‡∏ö‡∏õ‡∏£‡∏∞‡∏Å‡∏≤‡∏®‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢‡πÅ‡∏•‡πâ‡∏ß');
                          navigate('/dashboard/company');
                        } catch {
                          alert('‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡∏•‡∏ö');
                        }
                      }
                    }}
                    className="rounded-full bg-rose-600 hover:bg-rose-700 text-white px-4 py-2 text-sm font-semibold shadow-sm"
                  >
                    üóëÔ∏è ‡∏•‡∏ö
                  </button>
                </>
              )}

              {userRole === 'student' && !fromStatusPage && !hasApplied && (
                <button
                  onClick={() => setShowModal(true)}
                  className="rounded-full bg-emerald-600 hover:bg-emerald-700 text-white px-5 py-2 font-semibold shadow-sm"
                >
                  ‚úÖ ‡∏™‡∏°‡∏±‡∏Ñ‡∏£
                </button>
              )}

              {userRole === 'student' && hasApplied && (
                <p className="text-emerald-600 font-semibold">‚úÖ ‡∏Ñ‡∏∏‡∏ì‡πÑ‡∏î‡πâ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏á‡∏≤‡∏ô‡∏ô‡∏µ‡πâ‡πÅ‡∏•‡πâ‡∏ß</p>
              )}
            </div>
          </div>

          <div className="bg-white rounded-2xl border border-[#E6F0FF] shadow-sm p-5">
            <h4 className="font-bold text-[#130347] mb-3">‡πÅ‡∏ú‡∏ô‡∏ó‡∏µ‡πà</h4>
            {isEmbedMap ? (
              <div className="overflow-hidden rounded-xl border border-[#E6F0FF]">
                <iframe
                  src={job.google_maps_link}
                  width="100%"
                  height="220"
                  style={{ border: 0 }}
                  allowFullScreen
                  loading="lazy"
