import { useState } from 'react';
import axios from 'axios';
import { useSearchParams, useNavigate } from 'react-router-dom';

const ResetPassword = () => {
  const [password, setPassword] = useState('');
  const [tokenValid, setTokenValid] = useState(true);
  const [searchParams] = useSearchParams();
  const token = searchParams.get('token');
  const navigate = useNavigate();
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/reset-password', { token, password });
      setMessage(res.data.message);
      setTimeout(() => navigate('/login'), 2000);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Token ไม่ถูกต้องหรือหมดอายุ');
      setTokenValid(false);
    }
  };

  return (
    <div className="min-h-screen flex">
      {/* ฝั่งซ้าย โลโก้ */}
      <div className="w-1/2 bg-[#6EC7E2] flex items-center justify-center">
        <div className="text-center">
          <img
            src="/uploads/InternConnectLogo2.png"
            alt="InternConnect Logo"
            className="mx-auto w-40 h-40"
          />
          <h1 className="text-white text-4xl font-bold mt-4">Intern Connect</h1>
        </div>
      </div>

      {/* ฝั่งขวา ฟอร์ม */}
      <div className="w-1/2 flex items-center justify-center bg-white">
        <div className="w-full max-w-md p-8">
          <h2 className="text-2xl font-bold text-center mb-2 text-[#225EC4]">ตั้งรหัสผ่านใหม่</h2>
          <p className="text-center text-sm mb-6 text-[#465d71]">
            กรุณากรอกรหัสผ่านใหม่ของคุณ
          </p>

          {tokenValid ? (
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="password"
                placeholder="รหัสผ่านใหม่"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#225EC4]"
              />
              <button
                type="submit"
                className="w-full bg-[#06B6D4] hover:bg-[#0e7490] text-white font-semibold py-2 rounded-lg transition"
              >
                รีเซ็ตรหัสผ่าน
              </button>
            </form>
          ) : (
            <p className="text-red-600 text-center font-medium">{message}</p>
          )}

          {message && tokenValid && (
            <p className="text-green-600 text-center mt-4 font-medium">{message}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResetPassword;
